import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Port is handled in routes.ts
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { miningEngine } from "./services/miningEngine";
import { gpuManager } from "./services/gpuManager";
import { merkleTreeService } from "./services/merkleTreeService";
import { miningPoolService } from "./services/miningPoolService";
import { analyticsService } from "./services/analyticsService";
import { alertService } from "./services/alertService";
import { configService } from "./services/configService";
import { adaptiveOptimizer } from "./services/adaptiveOptimizer";
import { quantumResistantCrypto } from "./services/quantumResistantCrypto";
import { clusterManager } from "./services/clusterManager";
import { miningPoolIntegration } from "./services/miningPoolIntegration";
import { validateRequest } from "./middleware/validation";
import { 
  insertGPUSchema, insertMiningPoolSchema, insertMerkleTreeConfigSchema,
  insertAlertSchema, insertSystemConfigSchema 
} from "@shared/schema";
import { logger } from "./utils/logger";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for real-time updates
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  const connectedClients = new Set<WebSocket>();

  wss.on('connection', (ws) => {
    logger.info('WebSocket client connected');
    connectedClients.add(ws);

    ws.on('close', () => {
      logger.info('WebSocket client disconnected');
      connectedClients.delete(ws);
    });

    ws.on('error', (error) => {
      logger.error('WebSocket error:', error);
      connectedClients.delete(ws);
    });
  });

  // Broadcast function for real-time updates
  const broadcast = (event: string, data: any) => {
    const message = JSON.stringify({ event, data, timestamp: new Date().toISOString() });
    connectedClients.forEach((client) => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(message);
      }
    });
  };

  // Initialize services with broadcast capability
  miningEngine.setBroadcast(broadcast);
  gpuManager.setBroadcast(broadcast);
  alertService.setBroadcast(broadcast);
  adaptiveOptimizer.setBroadcast(broadcast);
  quantumResistantCrypto.setBroadcast(broadcast);
  clusterManager.setBroadcast(broadcast);
  miningPoolIntegration.setBroadcast(broadcast);

  // Start background services
  await miningEngine.initialize();
  await gpuManager.initialize();
  await miningPoolService.initialize();
  await analyticsService.initialize();

  // Initialize advanced services
  await adaptiveOptimizer.initialize();
  await quantumResistantCrypto.initialize();
  await clusterManager.initialize();
  await miningPoolIntegration.initialize();

  // API Routes

  // Dashboard endpoints
  app.get('/api/dashboard/metrics', async (req, res) => {
    try {
      const metrics = await analyticsService.getDashboardMetrics();
      res.json(metrics);
    } catch (error) {
      logger.error('Error fetching dashboard metrics:', error);
      res.status(500).json({ error: 'Failed to fetch dashboard metrics' });
    }
  });

  app.get('/api/dashboard/chart-data/:period', async (req, res) => {
    try {
      const { period } = req.params;
      const hours = period === '24h' ? 24 : period === '7d' ? 168 : 720; // 24h, 7d, 30d
      const chartData = await analyticsService.getChartData(hours);
      res.json(chartData);
    } catch (error) {
      logger.error('Error fetching chart data:', error);
      res.status(500).json({ error: 'Failed to fetch chart data' });
    }
  });

  // GPU management endpoints
  app.get('/api/gpus', async (req, res) => {
    try {
      const gpus = await storage.getGPUs();
      res.json(gpus);
    } catch (error) {
      logger.error('Error fetching GPUs:', error);
      res.status(500).json({ error: 'Failed to fetch GPUs' });
    }
  });

  app.post('/api/gpus', validateRequest(insertGPUSchema), async (req, res) => {
    try {
      const gpu = await storage.createGPU(req.body);
      broadcast('gpu_added', gpu);
      res.status(201).json(gpu);
    } catch (error) {
      logger.error('Error creating GPU:', error);
      res.status(500).json({ error: 'Failed to create GPU' });
    }
  });

  app.put('/api/gpus/:id', async (req, res) => {
    try {
      const { id } = req.params;
      const gpu = await storage.updateGPU(id, req.body);
      if (!gpu) {
        return res.status(404).json({ error: 'GPU not found' });
      }
      broadcast('gpu_updated', gpu);
      res.json(gpu);
    } catch (error) {
      logger.error('Error updating GPU:', error);
      res.status(500).json({ error: 'Failed to update GPU' });
    }
  });

  app.delete('/api/gpus/:id', async (req, res) => {
    try {
      const { id } = req.params;
      const success = await storage.deleteGPU(id);
      if (!success) {
        return res.status(404).json({ error: 'GPU not found' });
      }
      broadcast('gpu_deleted', { id });
      res.status(204).send();
    } catch (error) {
      logger.error('Error deleting GPU:', error);
      res.status(500).json({ error: 'Failed to delete GPU' });
    }
  });

  app.post('/api/gpus/optimize', async (req, res) => {
    try {
      await gpuManager.optimizeAll();
      res.json({ message: 'GPU optimization started' });
    } catch (error) {
      logger.error('Error optimizing GPUs:', error);
      res.status(500).json({ error: 'Failed to optimize GPUs' });
    }
  });

  // Mining pool endpoints
  app.get('/api/mining-pools', async (req, res) => {
    try {
      const pools = await storage.getMiningPools();
      res.json(pools);
    } catch (error) {
      logger.error('Error fetching mining pools:', error);
      res.status(500).json({ error: 'Failed to fetch mining pools' });
    }
  });

  app.post('/api/mining-pools', validateRequest(insertMiningPoolSchema), async (req, res) => {
    try {
      const pool = await storage.createMiningPool(req.body);
      broadcast('pool_added', pool);
      res.status(201).json(pool);
    } catch (error) {
      logger.error('Error creating mining pool:', error);
      res.status(500).json({ error: 'Failed to create mining pool' });
    }
  });

  app.put('/api/mining-pools/:id/activate', async (req, res) => {
    try {
      const { id } = req.params;
      const success = await storage.setActiveMiningPool(id);
      if (!success) {
        return res.status(404).json({ error: 'Mining pool not found' });
      }
      await miningPoolService.switchToPool(id);
      broadcast('active_pool_changed', { id });
      res.json({ message: 'Mining pool activated' });
    } catch (error) {
      logger.error('Error activating mining pool:', error);
      res.status(500).json({ error: 'Failed to activate mining pool' });
    }
  });

  // Merkle tree configuration endpoints
  app.get('/api/merkle-configs', async (req, res) => {
    try {
      const configs = await storage.getMerkleTreeConfigs();
      res.json(configs);
    } catch (error) {
      logger.error('Error fetching merkle configs:', error);
      res.status(500).json({ error: 'Failed to fetch merkle configs' });
    }
  });

  app.post('/api/merkle-configs', validateRequest(insertMerkleTreeConfigSchema), async (req, res) => {
    try {
      const config = await storage.createMerkleTreeConfig(req.body);
      res.status(201).json(config);
    } catch (error) {
      logger.error('Error creating merkle config:', error);
      res.status(500).json({ error: 'Failed to create merkle config' });
    }
  });

  app.put('/api/merkle-configs/:id/activate', async (req, res) => {
    try {
      const { id } = req.params;
      const success = await storage.setActiveMerkleTreeConfig(id);
      if (!success) {
        return res.status(404).json({ error: 'Merkle config not found' });
      }
      await merkleTreeService.applyConfiguration(id);
      broadcast('merkle_config_changed', { id });
      res.json({ message: 'Merkle configuration activated' });
    } catch (error) {
      logger.error('Error activating merkle config:', error);
      res.status(500).json({ error: 'Failed to activate merkle config' });
    }
  });

  // Analytics endpoints
  app.get('/api/analytics/performance/:hours', async (req, res) => {
    try {
      const hours = parseInt(req.params.hours) || 24;
      const performance = await analyticsService.getPerformanceAnalytics(hours);
      res.json(performance);
    } catch (error) {
      logger.error('Error fetching performance analytics:', error);
      res.status(500).json({ error: 'Failed to fetch performance analytics' });
    }
  });

  app.get('/api/analytics/transactions/:hours', async (req, res) => {
    try {
      const hours = parseInt(req.params.hours) || 24;
      const stats = await storage.getTransactionStats(hours);
      res.json(stats);
    } catch (error) {
      logger.error('Error fetching transaction stats:', error);
      res.status(500).json({ error: 'Failed to fetch transaction stats' });
    }
  });

  // Alerts endpoints
  app.get('/api/alerts', async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 50;
      const alerts = await storage.getAlerts(limit);
      res.json(alerts);
    } catch (error) {
      logger.error('Error fetching alerts:', error);
      res.status(500).json({ error: 'Failed to fetch alerts' });
    }
  });

  app.get('/api/alerts/unread', async (req, res) => {
    try {
      const alerts = await storage.getUnreadAlerts();
      res.json(alerts);
    } catch (error) {
      logger.error('Error fetching unread alerts:', error);
      res.status(500).json({ error: 'Failed to fetch unread alerts' });
    }
  });

  app.post('/api/alerts', validateRequest(insertAlertSchema), async (req, res) => {
    try {
      const alert = await storage.createAlert(req.body);
      broadcast('new_alert', alert);
      res.status(201).json(alert);
    } catch (error) {
      logger.error('Error creating alert:', error);
      res.status(500).json({ error: 'Failed to create alert' });
    }
  });

  app.put('/api/alerts/:id/read', async (req, res) => {
    try {
      const { id } = req.params;
      const success = await storage.markAlertAsRead(id);
      if (!success) {
        return res.status(404).json({ error: 'Alert not found' });
      }
      broadcast('alert_read', { id });
      res.json({ message: 'Alert marked as read' });
    } catch (error) {
      logger.error('Error marking alert as read:', error);
      res.status(500).json({ error: 'Failed to mark alert as read' });
    }
  });

  app.delete('/api/alerts', async (req, res) => {
    try {
      await storage.clearAllAlerts();
      broadcast('alerts_cleared', {});
      res.json({ message: 'All alerts cleared' });
    } catch (error) {
      logger.error('Error clearing alerts:', error);
      res.status(500).json({ error: 'Failed to clear alerts' });
    }
  });

  // System configuration endpoints
  app.get('/api/configs', async (req, res) => {
    try {
      const configs = await storage.getSystemConfigs();
      res.json(configs);
    } catch (error) {
      logger.error('Error fetching system configs:', error);
      res.status(500).json({ error: 'Failed to fetch system configs' });
    }
  });

  app.put('/api/configs/:key', async (req, res) => {
    try {
      const { key } = req.params;
      const { value } = req.body;
      const config = await storage.updateSystemConfig(key, value);
      if (!config) {
        return res.status(404).json({ error: 'Configuration not found' });
      }
      await configService.applyConfiguration(key, value);
      broadcast('config_updated', config);
      res.json(config);
    } catch (error) {
      logger.error('Error updating system config:', error);
      res.status(500).json({ error: 'Failed to update system config' });
    }
  });

  app.post('/api/configs/apply', async (req, res) => {
    try {
      const configs = req.body;
      await configService.applyBulkConfiguration(configs);
      broadcast('bulk_config_applied', configs);
      res.json({ message: 'Configuration applied successfully' });
    } catch (error) {
      logger.error('Error applying configuration:', error);
      res.status(500).json({ error: 'Failed to apply configuration' });
    }
  });

  // System status endpoint
  app.get('/api/system/status', async (req, res) => {
    try {
      const status = {
        uptime: process.uptime(),
        memoryUsage: process.memoryUsage(),
        cpuUsage: process.cpuUsage(),
        activeConnections: connectedClients.size,
        services: {
          miningEngine: miningEngine.getStatus(),
          gpuManager: gpuManager.getStatus(),
          miningPools: miningPoolService.getStatus(),
          analytics: analyticsService.getStatus(),
        }
      };
      res.json(status);
    } catch (error) {
      logger.error('Error fetching system status:', error);
      res.status(500).json({ error: 'Failed to fetch system status' });
    }
  });

  // AI/ML Optimization endpoints
  app.get('/api/ai/optimization-status', async (req, res) => {
    try {
      const status = await adaptiveOptimizer.getOptimizationStatus();
      res.json(status);
    } catch (error) {
      logger.error('Error fetching optimization status:', error);
      res.status(500).json({ error: 'Failed to fetch optimization status' });
    }
  });

  app.get('/api/ai/performance-prediction/:hours', async (req, res) => {
    try {
      const hours = parseInt(req.params.hours);
      const prediction = await adaptiveOptimizer.getPerformancePrediction(hours);
      res.json(prediction);
    } catch (error) {
      logger.error('Error fetching performance prediction:', error);
      res.status(500).json({ error: 'Failed to fetch performance prediction' });
    }
  });

  // Quantum Security endpoints
  app.get('/api/quantum/security-assessment', async (req, res) => {
    try {
      const assessment = await quantumResistantCrypto.generateSecurityAssessment();
      res.json(assessment);
    } catch (error) {
      logger.error('Error generating security assessment:', error);
      res.status(500).json({ error: 'Failed to generate security assessment' });
    }
  });

  // Cluster Management endpoints
  app.get('/api/cluster/status', async (req, res) => {
    try {
      const status = await clusterManager.getClusterStatus();
      res.json(status);
    } catch (error) {
      logger.error('Error fetching cluster status:', error);
      res.status(500).json({ error: 'Failed to fetch cluster status' });
    }
  });

  // Mining Pool Integration endpoints
  app.get('/api/pools/integration-status', async (req, res) => {
    try {
      const status = await miningPoolIntegration.getIntegrationStatus();
      res.json(status);
    } catch (error) {
      logger.error('Error fetching pool integration status:', error);
      res.status(500).json({ error: 'Failed to fetch pool integration status' });
    }
  });

  const port = Number(process.env.PORT) || 5000;
  httpServer.listen(port, '0.0.0.0', () => {
    logger.info(`serving on port ${port}`);
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  users, gpus, miningPools, merkleTreeConfigs, systemMetrics, alerts, 
  transactionBatches, systemConfigs,
  type User, type InsertUser, type GPU, type InsertGPU, 
  type MiningPool, type InsertMiningPool, type MerkleTreeConfig, type InsertMerkleTreeConfig,
  type SystemMetric, type InsertSystemMetric, type Alert, type InsertAlert,
  type TransactionBatch, type InsertTransactionBatch, type SystemConfig, type InsertSystemConfig
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, lte, count, avg, sum } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // GPU operations
  getGPUs(): Promise<GPU[]>;
  getGPU(id: string): Promise<GPU | undefined>;
  createGPU(gpu: InsertGPU): Promise<GPU>;
  updateGPU(id: string, updates: Partial<GPU>): Promise<GPU | undefined>;
  deleteGPU(id: string): Promise<boolean>;

  // Mining Pool operations
  getMiningPools(): Promise<MiningPool[]>;
  getActiveMiningPool(): Promise<MiningPool | undefined>;
  createMiningPool(pool: InsertMiningPool): Promise<MiningPool>;
  updateMiningPool(id: string, updates: Partial<MiningPool>): Promise<MiningPool | undefined>;
  deleteMiningPool(id: string): Promise<boolean>;
  setActiveMiningPool(id: string): Promise<boolean>;

  // Merkle Tree Config operations
  getMerkleTreeConfigs(): Promise<MerkleTreeConfig[]>;
  getActiveMerkleTreeConfig(): Promise<MerkleTreeConfig | undefined>;
  createMerkleTreeConfig(config: InsertMerkleTreeConfig): Promise<MerkleTreeConfig>;
  updateMerkleTreeConfig(id: string, updates: Partial<MerkleTreeConfig>): Promise<MerkleTreeConfig | undefined>;
  setActiveMerkleTreeConfig(id: string): Promise<boolean>;

  // System Metrics operations
  getLatestSystemMetrics(): Promise<SystemMetric | undefined>;
  getSystemMetricsHistory(hours: number): Promise<SystemMetric[]>;
  createSystemMetric(metric: InsertSystemMetric): Promise<SystemMetric>;

  // Alerts operations
  getAlerts(limit?: number): Promise<Alert[]>;
  getUnreadAlerts(): Promise<Alert[]>;
  createAlert(alert: InsertAlert): Promise<Alert>;
  markAlertAsRead(id: string): Promise<boolean>;
  markAlertAsResolved(id: string): Promise<boolean>;
  clearAllAlerts(): Promise<boolean>;

  // Transaction Batch operations
  getTransactionBatches(limit?: number): Promise<TransactionBatch[]>;
  createTransactionBatch(batch: InsertTransactionBatch): Promise<TransactionBatch>;
  getTransactionStats(hours: number): Promise<{
    totalProcessed: number;
    averageEfficiency: number;
    averageProcessingTime: number;
  }>;

  // System Config operations
  getSystemConfigs(): Promise<SystemConfig[]>;
  getSystemConfig(key: string): Promise<SystemConfig | undefined>;
  setSystemConfig(config: InsertSystemConfig): Promise<SystemConfig>;
  updateSystemConfig(key: string, value: any): Promise<SystemConfig | undefined>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  // GPU operations
  async getGPUs(): Promise<GPU[]> {
    return await db.select().from(gpus).orderBy(gpus.name);
  }

  async getGPU(id: string): Promise<GPU | undefined> {
    const [gpu] = await db.select().from(gpus).where(eq(gpus.id, id));
    return gpu || undefined;
  }

  async createGPU(gpu: InsertGPU): Promise<GPU> {
    const [created] = await db.insert(gpus).values(gpu).returning();
    return created;
  }

  async updateGPU(id: string, updates: Partial<GPU>): Promise<GPU | undefined> {
    const [updated] = await db.update(gpus)
      .set({ ...updates, lastSeen: new Date() })
      .where(eq(gpus.id, id))
      .returning();
    return updated || undefined;
  }

  async deleteGPU(id: string): Promise<boolean> {
    const result = await db.delete(gpus).where(eq(gpus.id, id));
    return (result.rowCount || 0) > 0;
  }

  // Mining Pool operations
  async getMiningPools(): Promise<MiningPool[]> {
    return await db.select().from(miningPools).orderBy(miningPools.priority);
  }

  async getActiveMiningPool(): Promise<MiningPool | undefined> {
    const [pool] = await db.select().from(miningPools).where(eq(miningPools.isActive, true));
    return pool || undefined;
  }

  async createMiningPool(pool: InsertMiningPool): Promise<MiningPool> {
    const [created] = await db.insert(miningPools).values(pool).returning();
    return created;
  }

  async updateMiningPool(id: string, updates: Partial<MiningPool>): Promise<MiningPool | undefined> {
    const [updated] = await db.update(miningPools)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(miningPools.id, id))
      .returning();
    return updated || undefined;
  }

  async deleteMiningPool(id: string): Promise<boolean> {
    const result = await db.delete(miningPools).where(eq(miningPools.id, id));
    return (result.rowCount || 0) > 0;
  }

  async setActiveMiningPool(id: string): Promise<boolean> {
    await db.update(miningPools).set({ isActive: false });
    const [updated] = await db.update(miningPools)
      .set({ isActive: true })
      .where(eq(miningPools.id, id))
      .returning();
    return !!updated;
  }

  // Merkle Tree Config operations
  async getMerkleTreeConfigs(): Promise<MerkleTreeConfig[]> {
    return await db.select().from(merkleTreeConfigs).orderBy(desc(merkleTreeConfigs.createdAt));
  }

  async getActiveMerkleTreeConfig(): Promise<MerkleTreeConfig | undefined> {
    const [config] = await db.select().from(merkleTreeConfigs).where(eq(merkleTreeConfigs.isActive, true));
    return config || undefined;
  }

  async createMerkleTreeConfig(config: InsertMerkleTreeConfig): Promise<MerkleTreeConfig> {
    const [created] = await db.insert(merkleTreeConfigs).values(config).returning();
    return created;
  }

  async updateMerkleTreeConfig(id: string, updates: Partial<MerkleTreeConfig>): Promise<MerkleTreeConfig | undefined> {
    const [updated] = await db.update(merkleTreeConfigs)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(merkleTreeConfigs.id, id))
      .returning();
    return updated || undefined;
  }

  async setActiveMerkleTreeConfig(id: string): Promise<boolean> {
    await db.update(merkleTreeConfigs).set({ isActive: false });
    const [updated] = await db.update(merkleTreeConfigs)
      .set({ isActive: true })
      .where(eq(merkleTreeConfigs.id, id))
      .returning();
    return !!updated;
  }

  // System Metrics operations
  async getLatestSystemMetrics(): Promise<SystemMetric | undefined> {
    const [metric] = await db.select().from(systemMetrics)
      .orderBy(desc(systemMetrics.timestamp))
      .limit(1);
    return metric || undefined;
  }

  async getSystemMetricsHistory(hours: number): Promise<SystemMetric[]> {
    const since = new Date(Date.now() - hours * 60 * 60 * 1000);
    return await db.select().from(systemMetrics)
      .where(gte(systemMetrics.timestamp, since))
      .orderBy(systemMetrics.timestamp);
  }

  async createSystemMetric(metric: InsertSystemMetric): Promise<SystemMetric> {
    const [created] = await db.insert(systemMetrics).values(metric).returning();
    return created;
  }

  // Alerts operations
  async getAlerts(limit: number = 50): Promise<Alert[]> {
    return await db.select().from(alerts)
      .orderBy(desc(alerts.createdAt))
      .limit(limit);
  }

  async getUnreadAlerts(): Promise<Alert[]> {
    return await db.select().from(alerts)
      .where(eq(alerts.isRead, false))
      .orderBy(desc(alerts.createdAt));
  }

  async createAlert(alert: InsertAlert): Promise<Alert> {
    const [created] = await db.insert(alerts).values(alert).returning();
    return created;
  }

  async markAlertAsRead(id: string): Promise<boolean> {
    const [updated] = await db.update(alerts)
      .set({ isRead: true })
      .where(eq(alerts.id, id))
      .returning();
    return !!updated;
  }

  async markAlertAsResolved(id: string): Promise<boolean> {
    const [updated] = await db.update(alerts)
      .set({ isResolved: true, resolvedAt: new Date() })
      .where(eq(alerts.id, id))
      .returning();
    return !!updated;
  }

  async clearAllAlerts(): Promise<boolean> {
    const result = await db.update(alerts).set({ isRead: true });
    return (result.rowCount || 0) > 0;
  }

  // Transaction Batch operations
  async getTransactionBatches(limit: number = 100): Promise<TransactionBatch[]> {
    return await db.select().from(transactionBatches)
      .orderBy(desc(transactionBatches.createdAt))
      .limit(limit);
  }

  async createTransactionBatch(batch: InsertTransactionBatch): Promise<TransactionBatch> {
    const [created] = await db.insert(transactionBatches).values(batch).returning();
    return created;
  }

  async getTransactionStats(hours: number): Promise<{
    totalProcessed: number;
    averageEfficiency: number;
    averageProcessingTime: number;
  }> {
    const since = new Date(Date.now() - hours * 60 * 60 * 1000);
    const [stats] = await db.select({
      totalProcessed: sum(transactionBatches.batchSize),
      averageEfficiency: avg(transactionBatches.efficiency),
      averageProcessingTime: avg(transactionBatches.processingTime),
    }).from(transactionBatches)
      .where(gte(transactionBatches.createdAt, since));

    return {
      totalProcessed: Number(stats.totalProcessed) || 0,
      averageEfficiency: Number(stats.averageEfficiency) || 0,
      averageProcessingTime: Number(stats.averageProcessingTime) || 0,
    };
  }

  // System Config operations
  async getSystemConfigs(): Promise<SystemConfig[]> {
    return await db.select().from(systemConfigs).orderBy(systemConfigs.category, systemConfigs.key);
  }

  async getSystemConfig(key: string): Promise<SystemConfig | undefined> {
    const [config] = await db.select().from(systemConfigs).where(eq(systemConfigs.key, key));
    return config || undefined;
  }

  async setSystemConfig(config: InsertSystemConfig): Promise<SystemConfig> {
    const [created] = await db.insert(systemConfigs)
      .values(config)
      .onConflictDoUpdate({
        target: systemConfigs.key,
        set: { value: config.value, updatedAt: new Date() }
      })
      .returning();
    return created;
  }

  async updateSystemConfig(key: string, value: any): Promise<SystemConfig | undefined> {
    const [updated] = await db.update(systemConfigs)
      .set({ value, updatedAt: new Date() })
      .where(eq(systemConfigs.key, key))
      .returning();
    return updated || undefined;
  }
}

export const storage = new DatabaseStorage();

