  /* Header styles */
  .header-gradient {
    background: linear-gradient(135deg, hsl(215, 28%, 17%) 0%, hsl(217, 33%, 10%) 100%);
  }

  /* Button variants */
  .btn-optimize {
    @apply bg-emerald-500 hover:bg-emerald-600 text-white font-medium transition-colors;
  }

  .btn-warning {
    @apply bg-amber-500 hover:bg-amber-600 text-white font-medium transition-colors;
  }

  .btn-danger {
    @apply bg-red-500 hover:bg-red-600 text-white font-medium transition-colors;
  }

  /* Data visualization styles */
  .hash-rate-chart {
    @apply w-full h-64;
  }

  .efficiency-meter {
    @apply w-full h-2 bg-slate-700 rounded-full overflow-hidden;
  }

  .efficiency-bar {
    @apply h-full transition-all duration-500 ease-out rounded-full;
  }

  .efficiency-excellent {
    @apply bg-emerald-500;
  }

  .efficiency-good {
    @apply bg-blue-500;
  }

  .efficiency-fair {
    @apply bg-amber-500;
  }

  .efficiency-poor {
    @apply bg-red-500;
