import { Request, Response, NextFunction } from 'express';
import { z, ZodSchema } from 'zod';
import { logger } from '../utils/logger';

export function validateRequest<T>(schema: ZodSchema<T>) {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      const validatedData = schema.parse(req.body);
      req.body = validatedData;
      next();
    } catch (error) {
      if (error instanceof z.ZodError) {
        const errorMessages = error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message,
          code: err.code,
        }));

        logger.warn('Request validation failed', {
          path: req.path,
          method: req.method,
          errors: errorMessages,
          body: req.body,
        });

        return res.status(400).json({
          error: 'Validation failed',
          details: errorMessages,
        });
      }

      logger.error('Unexpected validation error', {
        path: req.path,
        method: req.method,
        error: error instanceof Error ? error.message : 'Unknown error',
      });

      return res.status(500).json({
        error: 'Internal validation error',
      });
    }
  };
}

export function validateQuery<T>(schema: ZodSchema<T>) {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      const validatedData = schema.parse(req.query);
      req.query = validatedData as any;
      next();
    } catch (error) {
      if (error instanceof z.ZodError) {
        const errorMessages = error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message,
          code: err.code,
        }));

        logger.warn('Query validation failed', {
          path: req.path,
          method: req.method,
          errors: errorMessages,
          query: req.query,
        });

        return res.status(400).json({
          error: 'Query validation failed',
          details: errorMessages,
        });
      }

      return res.status(500).json({
        error: 'Internal validation error',
      });
    }
  };
}

export function validateParams<T>(schema: ZodSchema<T>) {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      const validatedData = schema.parse(req.params);
      req.params = validatedData as any;
      next();
    } catch (error) {
      if (error instanceof z.ZodError) {
        const errorMessages = error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message,
          code: err.code,
        }));

        logger.warn('Params validation failed', {
          path: req.path,
          method: req.method,
          errors: errorMessages,
          params: req.params,
        });

        return res.status(400).json({
          error: 'Parameter validation failed',
          details: errorMessages,
        });
      }

      return res.status(500).json({
        error: 'Internal validation error',
      });
    }
  };
}

// Common validation schemas
export const commonSchemas = {
  uuid: z.string().uuid('Invalid UUID format'),
  positiveInteger: z.number().int().positive('Must be a positive integer'),
  percentage: z.number().min(0).max(100, 'Must be between 0 and 100'),
  timestamp: z.string().datetime('Invalid timestamp format'),

  // Pagination
  pagination: z.object({
    page: z.coerce.number().int().min(1).default(1),
    limit: z.coerce.number().int().min(1).max(100).default(20),
  }),

  // Sorting
  sort: z.object({
    field: z.string().min(1),
    direction: z.enum(['asc', 'desc']).default('asc'),
  }),

  // Date range
  dateRange: z.object({
    start: z.string().datetime(),
    end: z.string().datetime(),
  }).refine(data => new Date(data.start) < new Date(data.end), {
    message: 'Start date must be before end date',
  }),
};

// GPU validation schemas
export const gpuValidationSchemas = {
  createGPU: z.object({
    name: z.string().min(1, 'GPU name is required'),
    model: z.string().min(1, 'GPU model is required'),
    hashRate: z.number().min(0, 'Hash rate must be non-negative'),
    temperature: z.number().int().min(0).max(150, 'Temperature must be between 0-150Â°C'),
    power: z.number().int().min(0).max(1000, 'Power must be between 0-1000W'),
    memoryUsed: z.number().min(0, 'Memory used must be non-negative'),
    memoryTotal: z.number().positive('Total memory must be positive'),
    utilizationRate: z.number().min(0).max(100, 'Utilization rate must be between 0-100%'),
    status: z.enum(['online', 'offline', 'warning', 'error']),
  }),

  updateGPU: z.object({
    name: z.string().min(1).optional(),
    hashRate: z.number().min(0).optional(),
    temperature: z.number().int().min(0).max(150).optional(),
    power: z.number().int().min(0).max(1000).optional(),
    memoryUsed: z.number().min(0).optional(),
    utilizationRate: z.number().min(0).max(100).optional(),
    status: z.enum(['online', 'offline', 'warning', 'error']).optional(),
  }),
};

// Mining pool validation schemas
export const poolValidationSchemas = {
  createPool: z.object({
    name: z.string().min(1, 'Pool name is required'),
    url: z.string().url('Invalid URL format'),
    port: z.number().int().min(1).max(65535, 'Port must be between 1-65535'),
    username: z.string().min(1, 'Username is required'),
    password: z.string().optional(),
    priority: z.number().int().min(1).max(10, 'Priority must be between 1-10'),
  }),

  updatePool: z.object({
    name: z.string().min(1).optional(),
    url: z.string().url().optional(),
    port: z.number().int().min(1).max(65535).optional(),
    username: z.string().min(1).optional(),
    password: z.string().optional(),
    priority: z.number().int().min(1).max(10).optional(),
    isActive: z.boolean().optional(),
  }),
};

// Configuration validation schemas
export const configValidationSchemas = {
  updateConfig: z.object({
    value: z.unknown(), // Will be validated based on config key
  }),

  bulkConfig: z.record(z.string(), z.unknown()),

  // Specific config value validators
  parallelThreads: z.number().int().min(1).max(2048),
  treeDepth: z.union([
    z.literal('auto'),
    z.literal('8'),
    z.literal('16'),
    z.literal('32'),
  ]),
  cacheStrategy: z.enum(['lru', 'lfu', 'adaptive']),
  memoryAllocation: z.number().min(50).max(90),
  failoverTimeout: z.number().int().min(10).max(300),
  maxConnections: z.number().int().min(1).max(100),
};

// Alert validation schemas
export const alertValidationSchemas = {
  createAlert: z.object({
    type: z.enum(['info', 'warning', 'error', 'success']),
    title: z.string().min(1, 'Title is required'),
    message: z.string().min(1, 'Message is required'),
    source: z.string().optional(),
    sourceId: z.string().uuid().optional(),
    severity: z.enum(['low', 'medium', 'high', 'critical']),
  }),
};

// System metrics validation schemas
export const metricsValidationSchemas = {
  createMetric: z.object({
    totalHashRate: z.number().min(0),
    treeEfficiency: z.number().min(0).max(100),
    activeGPUs: z.number().int().min(0),
    dailyRevenue: z.number().min(0),
    cpuUsage: z.number().min(0).max(100),
    memoryUsage: z.number().min(0).max(100),
    storageUsage: z.number().min(0).max(100),
    networkLoad: z.number().min(0).max(100),
    transactionsProcessed: z.number().int().min(0),
    transactionsPending: z.number().int().min(0),
  }),
};

// Error handling middleware
export function handleValidationError(error: any, req: Request, res: Response, next: NextFunction) {
  if (error instanceof z.ZodError) {
    logger.warn('Validation error occurred', {
      path: req.path,
      method: req.method,
      errors: error.errors,
    });

    return res.status(400).json({
      error: 'Validation failed',
      details: error.errors.map(err => ({
        field: err.path.join('.'),
        message: err.message,
        code: err.code,
      })),
    });
  }

  next(error);
}

// Rate limiting validation
export function validateRateLimit(maxRequests: number = 100, windowMs: number = 60000) {
  const requests = new Map<string, number[]>();

  return (req: Request, res: Response, next: NextFunction) => {
    const clientIp = req.ip || req.connection.remoteAddress || 'unknown';
    const now = Date.now();

    if (!requests.has(clientIp)) {
      requests.set(clientIp, []);
    }

    const clientRequests = requests.get(clientIp)!;

    // Remove old requests outside the window
    const validRequests = clientRequests.filter(timestamp => now - timestamp < windowMs);

    if (validRequests.length >= maxRequests) {
      logger.warn('Rate limit exceeded', {
        clientIp,
        requests: validRequests.length,
        maxRequests,
        windowMs,
        path: req.path,
      });

      return res.status(429).json({
        error: 'Rate limit exceeded',
        message: `Too many requests. Maximum ${maxRequests} requests per ${windowMs / 1000} seconds.`,
        retryAfter: Math.ceil(windowMs / 1000),
      });
    }

    validRequests.push(now);
    requests.set(clientIp, validRequests);

    next();
  };
}

server/services/alertService.ts.content:
import { storage } from "../storage";
import { logger } from "../utils/logger";
import type { InsertAlert } from "@shared/schema";

interface AlertStats {
  total: number;
  unread: number;
  critical: number;
  high: number;
  medium: number;
  low: number;
  byType: Record<string, number>;
  bySource: Record<string, number>;
}

class AlertService {
  private isInitialized = false;
  private broadcast?: (event: string, data: any) => void;
  private alertQueue: InsertAlert[] = [];
  private processingInterval?: NodeJS.Timeout;
  private recentAlerts: Set<string> = new Set(); // Prevent duplicate alerts
  private alertThrottles: Map<string, Date> = new Map(); // Throttle similar alerts

  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    logger.info('Initializing Alert Service...');

    try {
      // Start alert processing queue
      this.startAlertProcessing();

      // Clean up old throttles periodically
      this.startThrottleCleanup();

      this.isInitialized = true;
      logger.info('Alert Service initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize Alert Service:', error);
      throw error;
    }
  }

  setBroadcast(broadcastFn: (event: string, data: any) => void): void {
    this.broadcast = broadcastFn;
  }

  private startAlertProcessing(): void {
    this.processingInterval = setInterval(async () => {
      await this.processAlertQueue();
    }, 1000); // Process alerts every second
  }

  private startThrottleCleanup(): void {
    setInterval(() => {
      const now = new Date();
      const fiveMinutesAgo = new Date(now.getTime() - 5 * 60 * 1000);

      // Clean up throttles older than 5 minutes
      for (const [key, timestamp] of this.alertThrottles.entries()) {
        if (timestamp < fiveMinutesAgo) {
          this.alertThrottles.delete(key);
        }
      }

      // Clean up recent alerts older than 1 minute
      this.recentAlerts.clear();
    }, 60000); // Clean up every minute
  }

  private async processAlertQueue(): Promise<void> {
    if (this.alertQueue.length === 0) return;

    const alertsToProcess = this.alertQueue.splice(0, 10); // Process up to 10 alerts at once

    for (const alertData of alertsToProcess) {
      try {
        const alert = await storage.createAlert(alertData);

        // Broadcast to connected clients
        if (this.broadcast) {
          this.broadcast('new_alert', alert);
        }

        logger.info(`Alert created: ${alert.title} [${alert.severity}]`);
      } catch (error) {
        logger.error('Error processing alert:', error);
      }
    }
  }

  async createAlert(alertData: InsertAlert): Promise<void> {
    // Generate unique key for throttling
    const throttleKey = `${alertData.title}_${alertData.source}_${alertData.sourceId || 'global'}`;

    // Check if this alert should be throttled
    if (this.shouldThrottleAlert(throttleKey, alertData.severity)) {
      return;
    }

    // Add to processing queue
    this.alertQueue.push(alertData);

    // Update throttle timestamp
    this.alertThrottles.set(throttleKey, new Date());

    // Add to recent alerts set for immediate duplicate prevention
    this.recentAlerts.add(throttleKey);
  }

  private shouldThrottleAlert(throttleKey: string, severity: string): boolean {
    const now = new Date();
    const lastAlert = this.alertThrottles.get(throttleKey);

    if (!lastAlert) return false;

    // Throttle timeouts based on severity
    const throttleMinutes = this.getThrottleTimeout(severity);
    const throttleTime = new Date(lastAlert.getTime() + throttleMinutes * 60 * 1000);

    return now < throttleTime;
  }

  private getThrottleTimeout(severity: string): number {
    switch (severity) {
      case 'critical': return 1; // 1 minute for critical alerts
      case 'high': return 3;     // 3 minutes for high alerts
      case 'medium': return 5;   // 5 minutes for medium alerts
      case 'low': return 10;     // 10 minutes for low alerts
      default: return 5;
    }
  }

  async getAlertStats(): Promise<AlertStats> {
    try {
      const alerts = await storage.getAlerts(1000); // Get recent alerts for stats

      const stats: AlertStats = {
        total: alerts.length,
        unread: alerts.filter(a => !a.isRead).length,
        critical: alerts.filter(a => a.severity === 'critical').length,
        high: alerts.filter(a => a.severity === 'high').length,
        medium: alerts.filter(a => a.severity === 'medium').length,
        low: alerts.filter(a => a.severity === 'low').length,
        byType: {},
        bySource: {},
      };

      // Count by type
      for (const alert of alerts) {
        stats.byType[alert.type] = (stats.byType[alert.type] || 0) + 1;
        if (alert.source) {
          stats.bySource[alert.source] = (stats.bySource[alert.source] || 0) + 1;
        }
      }

      return stats;
    } catch (error) {
      logger.error('Error getting alert stats:', error);
      throw error;
    }
  }

  async createSystemAlert(title: string, message: string, severity: 'low' | 'medium' | 'high' | 'critical' = 'medium'): Promise<void> {
    await this.createAlert({
      type: 'info',
      title,
      message,
      source: 'system',
      severity,
    });
  }

  async createGPUAlert(gpuId: string, title: string, message: string, type: 'warning' | 'error' = 'warning'): Promise<void> {
    await this.createAlert({
      type,
      title,
      message,
      source: 'gpu',
      sourceId: gpuId,
      severity: type === 'error' ? 'high' : 'medium',
    });
  }

  async createPoolAlert(poolId: string, title: string, message: string, severity: 'low' | 'medium' | 'high' = 'medium'): Promise<void> {
    await this.createAlert({
      type: 'warning',
      title,
      message,
      source: 'pool',
      sourceId: poolId,
      severity,
    });
  }

  async createPerformanceAlert(metric: string, value: number, threshold: number): Promise<void> {
    const severity = value > threshold * 1.5 ? 'high' : value > threshold * 1.2 ? 'medium' : 'low';

    await this.createAlert({
      type: 'warning',
      title: `Performance Alert: ${metric}`,
      message: `${metric} is ${value.toFixed(2)} (threshold: ${threshold.toFixed(2)})`,
      source: 'system',
      severity,
    });
  }

  async resolveAlertsBySource(source: string, sourceId?: string): Promise<void> {
    try {
      const alerts = await storage.getAlerts();
      const alertsToResolve = alerts.filter(alert => 
        alert.source === source && 
        (!sourceId || alert.sourceId === sourceId) &&
        !alert.isResolved
      );

      for (const alert of alertsToResolve) {
        await storage.markAlertAsResolved(alert.id);
      }

      if (this.broadcast && alertsToResolve.length > 0) {
        this.broadcast('alerts_resolved', { source, sourceId, count: alertsToResolve.length });
      }

      logger.info(`Resolved ${alertsToResolve.length} alerts for ${source}${sourceId ? `:${sourceId}` : ''}`);
    } catch (error) {
      logger.error('Error resolving alerts by source:', error);
    }
  }

  async autoResolveOldAlerts(): Promise<void> {
    try {
      const twentyFourHoursAgo = new Date(Date.now() - 24 * 60 * 60 * 1000);
      const alerts = await storage.getAlerts(1000);

      const oldAlerts = alerts.filter(alert => 
        !alert.isResolved &&
        alert.severity === 'low' &&
        alert.createdAt &&
        alert.createdAt < twentyFourHoursAgo
      );

      for (const alert of oldAlerts) {
        await storage.markAlertAsResolved(alert.id);
      }

      if (oldAlerts.length > 0) {
        logger.info(`Auto-resolved ${oldAlerts.length} old low-severity alerts`);
      }
    } catch (error) {
      logger.error('Error auto-resolving old alerts:', error);
    }
  }

  // Predefined alert templates for common scenarios
  async createTemperatureAlert(gpuId: string, gpuName: string, temperature: number): Promise<void> {
    if (temperature > 90) {
      await this.createGPUAlert(
        gpuId,
        'Critical GPU Temperature',
        `${gpuName} is critically overheating at ${temperature}Â°C - immediate action required`,
        'error'
      );
    } else if (temperature > 85) {
      await this.createGPUAlert(
        gpuId,
        'High GPU Temperature',
        `${gpuName} is running hot at ${temperature}Â°C`,
        'warning'
      );
    }
  }

  async createHashRateAlert(hashRate: number, threshold: number): Promise<void> {
    const percentageDrop = ((threshold - hashRate) / threshold) * 100;

    await this.createAlert({
      type: 'warning',
      title: 'Hash Rate Drop Detected',
      message: `Hash rate dropped to ${hashRate.toFixed(1)} TH/s (${percentageDrop.toFixed(1)}% below threshold)`,
      source: 'system',
      severity: percentageDrop > 20 ? 'high' : percentageDrop > 10 ? 'medium' : 'low',
    });
  }

  async createEfficiencyAlert(efficiency: number): Promise<void> {
    if (efficiency < 80) {
      await this.createAlert({
        type: 'warning',
        title: 'Low Mining Efficiency',
        message: `Mining efficiency dropped to ${efficiency.toFixed(1)}% - optimization recommended`,
        source: 'system',
        severity: 'medium',
      });
    }
  }

  getStatus() {
    return {
      isInitialized: this.isInitialized,
      queueSize: this.alertQueue.length,
      activeThrottles: this.alertThrottles.size,
      recentAlertsCount: this.recentAlerts.size,
    };
  }

  async shutdown(): Promise<void> {
    logger.info('Shutting down Alert Service...');

    if (this.processingInterval) {
      clearInterval(this.processingInterval);
    }

    // Process remaining alerts
    await this.processAlertQueue();

    this.alertQueue = [];
    this.recentAlerts.clear();
    this.alertThrottles.clear();

    logger.info('Alert Service shut down successfully');
  }
}

export const alertService = new AlertService();

server/services/analyticsService.ts.content:
import { storage } from "../storage";
import { logger } from "../utils/logger";

interface DashboardMetrics {
  totalHashRate: number;
  hashRateChange: number;
  treeEfficiency: number;
  efficiencyChange: number;
  activeGPUs: string;
  dailyRevenue: number;
  revenueChange: number;
  systemHealth: string;
  cpuUsage: number;
  memoryUsage: number;
  storageUsage: number;
  networkLoad: number;
  transactionsProcessed: number;
  transactionsPending: number;
}

interface ChartDataPoint {
  timestamp: string;
  hashRate: number;
  efficiency: number;
  temperature: number;
  power: number;
}

interface PerformanceAnalytics {
  averageHashRate: number;
  peakHashRate: number;
  averageEfficiency: number;
  uptimePercentage: number;
  totalTransactions: number;
  averageProcessingTime: number;
  errorRate: number;
}

class AnalyticsService {
  private isInitialized = false;
  private previousMetrics: any = {};

  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    logger.info('Initializing Analytics Service...');

    try {
      // Load previous metrics for comparison
      const latestMetrics = await storage.getLatestSystemMetrics();
      if (latestMetrics) {
        this.previousMetrics = latestMetrics;
      }

      this.isInitialized = true;
      logger.info('Analytics Service initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize Analytics Service:', error);
      throw error;
    }
  }

  async getDashboardMetrics(): Promise<DashboardMetrics> {
    try {
      const currentMetrics = await storage.getLatestSystemMetrics();
      const gpus = await storage.getGPUs();

      if (!currentMetrics) {
        throw new Error('No metrics available');
      }

      const activeGPUs = gpus.filter(gpu => gpu.status === 'online');
      const totalGPUs = gpus.length;

      // Calculate percentage changes
      const hashRateChange = this.calculatePercentageChange(
        currentMetrics.totalHashRate,
        this.previousMetrics.totalHashRate || currentMetrics.totalHashRate
      );

      const efficiencyChange = this.calculatePercentageChange(
        currentMetrics.treeEfficiency,
        this.previousMetrics.treeEfficiency || currentMetrics.treeEfficiency
      );

      const revenueChange = this.calculatePercentageChange(
        currentMetrics.dailyRevenue,
        this.previousMetrics.dailyRevenue || currentMetrics.dailyRevenue
      );

      // System health assessment
      const systemHealth = this.assessSystemHealth(currentMetrics, gpus);

      return {
        totalHashRate: Math.round(currentMetrics.totalHashRate * 10) / 10,
        hashRateChange,
        treeEfficiency: Math.round(currentMetrics.treeEfficiency * 10) / 10,
        efficiencyChange,
        activeGPUs: `${activeGPUs.length}/${totalGPUs}`,
        dailyRevenue: Math.round(currentMetrics.dailyRevenue * 100) / 100,
        revenueChange,
        systemHealth,
        cpuUsage: Math.round(currentMetrics.cpuUsage * 10) / 10,
        memoryUsage: Math.round(currentMetrics.memoryUsage * 10) / 10,
        storageUsage: Math.round(currentMetrics.storageUsage * 10) / 10,
        networkLoad: Math.round(currentMetrics.networkLoad * 10) / 10,
        transactionsProcessed: currentMetrics.transactionsProcessed,
        transactionsPending: currentMetrics.transactionsPending,
      };
    } catch (error) {
      logger.error('Error getting dashboard metrics:', error);
      throw error;
    }
  }

  async getChartData(hours: number): Promise<ChartDataPoint[]> {
    try {
      const metrics = await storage.getSystemMetricsHistory(hours);
      const gpus = await storage.getGPUs();

      return metrics.map(metric => {
        // Calculate average GPU temperature and power
        const avgTemperature = gpus.length > 0 
          ? gpus.reduce((sum, gpu) => sum + gpu.temperature, 0) / gpus.length
          : 65;

        const avgPower = gpus.length > 0
          ? gpus.reduce((sum, gpu) => sum + gpu.power, 0) / gpus.length
          : 300;

        return {
          timestamp: metric.timestamp?.toISOString() || new Date().toISOString(),
          hashRate: Math.round(metric.totalHashRate * 10) / 10,
          efficiency: Math.round(metric.treeEfficiency * 10) / 10,
          temperature: Math.round(avgTemperature),
          power: Math.round(avgPower),
        };
      });
    } catch (error) {
      logger.error('Error getting chart data:', error);
      throw error;
    }
  }

  async getPerformanceAnalytics(hours: number): Promise<PerformanceAnalytics> {
    try {
      const metrics = await storage.getSystemMetricsHistory(hours);
      const transactionStats = await storage.getTransactionStats(hours);
      const gpus = await storage.getGPUs();

      if (metrics.length === 0) {
        throw new Error('No metrics available for the specified period');
      }

      // Calculate averages and peaks
      const hashRates = metrics.map(m => m.totalHashRate);
      const efficiencies = metrics.map(m => m.treeEfficiency);

      const averageHashRate = hashRates.reduce((sum, rate) => sum + rate, 0) / hashRates.length;
      const peakHashRate = Math.max(...hashRates);
      const averageEfficiency = efficiencies.reduce((sum, eff) => sum + eff, 0) / efficiencies.length;

      // Calculate uptime percentage based on GPU status
      const onlineGPUs = gpus.filter(gpu => gpu.status === 'online').length;
      const uptimePercentage = gpus.length > 0 ? (onlineGPUs / gpus.length) * 100 : 0;

      // Error rate calculation (based on GPU errors and system issues)
      const errorGPUs = gpus.filter(gpu => gpu.status === 'error').length;
      const errorRate = gpus.length > 0 ? (errorGPUs / gpus.length) * 100 : 0;

      return {
        averageHashRate: Math.round(averageHashRate * 10) / 10,
        peakHashRate: Math.round(peakHashRate * 10) / 10,
        averageEfficiency: Math.round(averageEfficiency * 10) / 10,
        uptimePercentage: Math.round(uptimePercentage * 10) / 10,
        totalTransactions: transactionStats.totalProcessed,
        averageProcessingTime: Math.round(transactionStats.averageProcessingTime * 1000) / 1000,
        errorRate: Math.round(errorRate * 10) / 10,
      };
    } catch (error) {
      logger.error('Error getting performance analytics:', error);
      throw error;
    }
  }

  private calculatePercentageChange(current: number, previous: number): number {
    if (previous === 0) return 0;
    return Math.round(((current - previous) / previous) * 1000) / 10;
  }

  private assessSystemHealth(metrics: any, gpus: any[]): string {
    let healthScore = 100;

    // GPU health impact
    const errorGPUs = gpus.filter(gpu => gpu.status === 'error').length;
    const warningGPUs = gpus.filter(gpu => gpu.status === 'warning').length;
    const offlineGPUs = gpus.filter(gpu => gpu.status === 'offline').length;

    healthScore -= errorGPUs * 15;
    healthScore -= warningGPUs * 8;
    healthScore -= offlineGPUs * 10;

    // System resource impact
    if (metrics.cpuUsage > 90) healthScore -= 15;
    else if (metrics.cpuUsage > 80) healthScore -= 8;

    if (metrics.memoryUsage > 95) healthScore -= 15;
    else if (metrics.memoryUsage > 85) healthScore -= 8;

    if (metrics.treeEfficiency < 80) healthScore -= 20;
    else if (metrics.treeEfficiency < 90) healthScore -= 10;

    // Determine health status
    if (healthScore >= 95) return 'Excellent';
    if (healthScore >= 85) return 'Good';
    if (healthScore >= 70) return 'Fair';
    if (healthScore >= 50) return 'Poor';
    return 'Critical';
  }

  async generateReport(type: 'daily' | 'weekly' | 'monthly'): Promise<any> {
    const hours = type === 'daily' ? 24 : type === 'weekly' ? 168 : 720;

    try {
      const [metrics, performance, transactionStats] = await Promise.all([
        this.getDashboardMetrics(),
        this.getPerformanceAnalytics(hours),
        storage.getTransactionStats(hours),
      ]);

      const report = {
        type,
        generatedAt: new Date().toISOString(),
        period: `Last ${hours} hours`,
        summary: {
          ...metrics,
          ...performance,
          ...transactionStats,
        },
        recommendations: this.generateRecommendations(metrics, performance),
      };

      logger.info(`Generated ${type} report`);
      return report;
    } catch (error) {
      logger.error('Error generating report:', error);
      throw error;
    }
  }

  private generateRecommendations(metrics: any, performance: any): string[] {
    const recommendations: string[] = [];

    if (performance.averageEfficiency < 90) {
      recommendations.push('Consider optimizing Merkle tree configuration to improve efficiency');
    }

    if (performance.errorRate > 5) {
      recommendations.push('High error rate detected - check GPU temperatures and power settings');
    }

    if (performance.uptimePercentage < 95) {
      recommendations.push('Uptime is below optimal - investigate GPU stability issues');
    }

    if (metrics.systemHealth !== 'Excellent' && metrics.systemHealth !== 'Good') {
      recommendations.push('System health requires attention - review alerts and system resources');
    }

    if (performance.averageProcessingTime > 0.1) {
      recommendations.push('Transaction processing time can be improved with parallel optimization');
    }

    if (recommendations.length === 0) {
      recommendations.push('System is performing optimally - maintain current configuration');
    }

    return recommendations;
  }

  getStatus() {
    return {
      isInitialized: this.isInitialized,
      hasPreviousMetrics: Object.keys(this.previousMetrics).length > 0,
    };
  }
}

export const analyticsService = new AnalyticsService();

server/services/configService.ts.content:
import { storage } from "../storage";
import { alertService } from "./alertService";
import { logger } from "../utils/logger";
import type { SystemConfig } from "@shared/schema";

interface ConfigurationContext {
  gpuCount: number;
  totalMemory: number;
  availableCores: number;
  networkBandwidth: number;
}

class ConfigService {
  private isInitialized = false;
  private activeConfigs: Map<string, any> = new Map();
  private configContext: ConfigurationContext = {
    gpuCount: 0,
    totalMemory: 0,
    availableCores: 0,
    networkBandwidth: 0,
  };

  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    logger.info('Initializing Configuration Service...');

    try {
      // Load existing configurations
      await this.loadConfigurations();

      // Initialize system context
      await this.initializeSystemContext();

      // Apply default configurations if none exist
      await this.ensureDefaultConfigurations();

      this.isInitialized = true;
      logger.info('Configuration Service initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize Configuration Service:', error);
      throw error;
    }
  }

  private async loadConfigurations(): Promise<void> {
    const configs = await storage.getSystemConfigs();

    for (const config of configs) {
      this.activeConfigs.set(config.key, config.value);
    }

    logger.info(`Loaded ${configs.length} system configurations`);
  }

  private async initializeSystemContext(): Promise<void> {
    // Get system information for intelligent configuration
    const gpus = await storage.getGPUs();

    this.configContext = {
      gpuCount: gpus.length,
      totalMemory: gpus.reduce((sum, gpu) => sum + gpu.memoryTotal, 0),
      availableCores: process.env.NODE_ENV === 'production' ? 32 : 16, // Simulate based on environment
      networkBandwidth: 1000, // Mbps - would be detected in real implementation
    };

    logger.info('System context initialized:', this.configContext);
  }

  private async ensureDefaultConfigurations(): Promise<void> {
    const defaultConfigs = [
      {
        key: 'mining.parallel_threads',
        value: this.calculateOptimalThreads(),
        category: 'algorithm',
        description: 'Number of parallel threads for Merkle tree construction',
      },
      {
        key: 'mining.tree_depth',
        value: 'auto',
        category: 'algorithm',
        description: 'Merkle tree depth optimization strategy',
      },
      {
        key: 'mining.cache_strategy',
        value: 'adaptive',
        category: 'algorithm',
        description: 'Cache strategy for tree node storage',
      },
      {
        key: 'gpu.memory_allocation',
        value: 75,
        category: 'hardware',
        description: 'GPU memory allocation percentage',
      },
      {
        key: 'gpu.cuda_enabled',
        value: true,
        category: 'hardware',
        description: 'Enable CUDA acceleration',
      },
      {
        key: 'gpu.power_management',
        value: true,
        category: 'hardware',
        description: 'Auto power management enabled',
      },
      {
        key: 'gpu.temperature_throttle',
        value: true,
        category: 'hardware',
        description: 'Temperature-based throttling enabled',
      },
      {
        key: 'network.failover_timeout',
        value: 30,
        category: 'network',
        description: 'Pool failover timeout in seconds',
      },
      {
        key: 'network.max_connections',
        value: 50,
        category: 'network',
        description: 'Maximum concurrent connections',
      },
      {
        key: 'network.load_balancing',
        value: true,
        category: 'network',
        description: 'Enable load balancing across pools',
      },
    ];

    for (const config of defaultConfigs) {
      const existing = await storage.getSystemConfig(config.key);
      if (!existing) {
        await storage.setSystemConfig(config);
        this.activeConfigs.set(config.key, config.value);
      }
    }
  }

  private calculateOptimalThreads(): number {
    const { gpuCount, availableCores } = this.configContext;

    // Base calculation on GPU cores and CPU cores
    const gpuThreads = gpuCount * 2048; // Approximate threads per GPU
    const cpuThreads = availableCores * 8; // Threads per CPU core

    // Calculate optimal thread count with diminishing returns
    const totalThreads = Math.min(gpuThreads + cpuThreads, 1024);

    // Ensure power of 2 for optimal parallel processing
    return Math.pow(2, Math.floor(Math.log2(totalThreads)));
  }

  async applyConfiguration(key: string, value: any): Promise<void> {
    logger.info(`Applying configuration: ${key} = ${JSON.stringify(value)}`);

    try {
      // Validate configuration value
      await this.validateConfiguration(key, value);

      // Store in database
      await storage.updateSystemConfig(key, value);

      // Update active config
      this.activeConfigs.set(key, value);

      // Apply configuration to relevant services
      await this.applyToServices(key, value);

      await alertService.createSystemAlert(
        'Configuration Updated',
        `${key} has been updated successfully`,
        'low'
      );

      logger.info(`Configuration applied successfully: ${key}`);
    } catch (error) {
      logger.error(`Failed to apply configuration ${key}:`, error);
      throw error;
    }
  }

  async applyBulkConfiguration(configs: Record<string, any>): Promise<void> {
    logger.info('Applying bulk configuration changes...');

    const errors: string[] = [];
    const applied: string[] = [];

    for (const [key, value] of Object.entries(configs)) {
      try {
        await this.applyConfiguration(key, value);
        applied.push(key);
      } catch (error) {
        errors.push(`${key}: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    }

    if (errors.length > 0) {
      await alertService.createSystemAlert(
        'Bulk Configuration Errors',
        `${errors.length} configuration(s) failed to apply: ${errors.join(', ')}`,
        'medium'
      );
    }

    if (applied.length > 0) {
      await alertService.createSystemAlert(
        'Bulk Configuration Applied',
        `Successfully applied ${applied.length} configuration(s)`,
        'low'
      );
    }

    logger.info(`Bulk configuration completed: ${applied.length} applied, ${errors.length} errors`);
  }

  private async validateConfiguration(key: string, value: any): Promise<void> {
    const validators: Record<string, (val: any) => boolean> = {
      'mining.parallel_threads': (val) => typeof val === 'number' && val >= 1 && val <= 2048,
      'mining.tree_depth': (val) => ['auto', '8', '16', '32'].includes(val),
      'mining.cache_strategy': (val) => ['lru', 'lfu', 'adaptive'].includes(val),
      'gpu.memory_allocation': (val) => typeof val === 'number' && val >= 50 && val <= 90,
      'gpu.cuda_enabled': (val) => typeof val === 'boolean',
      'gpu.power_management': (val) => typeof val === 'boolean',
      'gpu.temperature_throttle': (val) => typeof val === 'boolean',
      'network.failover_timeout': (val) => typeof val === 'number' && val >= 10 && val <= 300,
      'network.max_connections': (val) => typeof val === 'number' && val >= 1 && val <= 100,
      'network.load_balancing': (val) => typeof val === 'boolean',
    };

    const validator = validators[key];
    if (validator && !validator(value)) {
      throw new Error(`Invalid value for configuration ${key}: ${JSON.stringify(value)}`);
    }
  }

  private async applyToServices(key: string, value: any): Promise<void> {
    // Apply configuration changes to relevant services
    switch (key) {
      case 'mining.parallel_threads':
      case 'mining.tree_depth':
      case 'mining.cache_strategy':
        // Would trigger merkle tree service reconfiguration
        logger.info(`Merkle tree configuration updated: ${key}`);
        break;

      case 'gpu.memory_allocation':
      case 'gpu.cuda_enabled':
      case 'gpu.power_management':
      case 'gpu.temperature_throttle':
        // Would trigger GPU manager reconfiguration
        logger.info(`GPU configuration updated: ${key}`);
        break;

      case 'network.failover_timeout':
      case 'network.max_connections':
      case 'network.load_balancing':
        // Would trigger mining pool service reconfiguration
        logger.info(`Network configuration updated: ${key}`);
        break;
    }
  }

  getConfiguration(key: string): any {
    return this.activeConfigs.get(key);
  }

  getAllConfigurations(): Record<string, any> {
    return Object.fromEntries(this.activeConfigs.entries());
  }

  async optimizeConfiguration(): Promise<void> {
    logger.info('Starting intelligent configuration optimization...');

    try {
      // Get current system performance
      const metrics = await storage.getLatestSystemMetrics();
      if (!metrics) {
        throw new Error('No metrics available for optimization');
      }

      const optimizations: Record<string, any> = {};

      // Optimize parallel threads based on efficiency
      if (metrics.treeEfficiency < 90) {
        const currentThreads = this.getConfiguration('mining.parallel_threads') || 512;
        const optimalThreads = this.calculateOptimalThreadsBasedOnPerformance(metrics);

        if (optimalThreads !== currentThreads) {
          optimizations['mining.parallel_threads'] = optimalThreads;
        }
      }

      // Optimize cache strategy based on hit rate simulation
      const cacheEfficiency = this.simulateCacheEfficiency(metrics);
      if (cacheEfficiency.recommendedStrategy !== this.getConfiguration('mining.cache_strategy')) {
        optimizations['mining.cache_strategy'] = cacheEfficiency.recommendedStrategy;
      }

      // Optimize GPU memory allocation based on utilization
      const gpus = await storage.getGPUs();
      const avgMemoryUsage = gpus.reduce((sum, gpu) => sum + (gpu.memoryUsed / gpu.memoryTotal), 0) / gpus.length * 100;
      const currentAllocation = this.getConfiguration('gpu.memory_allocation') || 75;

      if (avgMemoryUsage > 85 && currentAllocation < 85) {
        optimizations['gpu.memory_allocation'] = Math.min(85, currentAllocation + 5);
      } else if (avgMemoryUsage < 60 && currentAllocation > 60) {
        optimizations['gpu.memory_allocation'] = Math.max(60, currentAllocation - 5);
      }

      // Apply optimizations
      if (Object.keys(optimizations).length > 0) {
        await this.applyBulkConfiguration(optimizations);

        await alertService.createSystemAlert(
          'Configuration Optimized',
          `Applied ${Object.keys(optimizations).length} optimization(s) based on performance analysis`,
          'low'
        );
      } else {
        await alertService.createSystemAlert(
          'Configuration Already Optimal',
          'Current configuration is already optimized for current performance metrics',
          'low'
        );
      }

      logger.info('Configuration optimization completed');
    } catch (error) {
      logger.error('Configuration optimization failed:', error);
      throw error;
    }
  }

  private calculateOptimalThreadsBasedOnPerformance(metrics: any): number {
    const efficiency = metrics.treeEfficiency;
    const currentThreads = this.getConfiguration('mining.parallel_threads') || 512;

    if (efficiency < 85) {
      // Reduce threads if efficiency is very low
      return Math.max(256, Math.floor(currentThreads * 0.8));
    } else if (efficiency > 95) {
      // Increase threads if efficiency is very high
      return Math.min(1024, Math.floor(currentThreads * 1.2));
    }

    return currentThreads;
  }

  private simulateCacheEfficiency(metrics: any): { recommendedStrategy: string; efficiency: number } {
    const memoryUsage = metrics.memoryUsage;
    const efficiency = metrics.treeEfficiency;

    if (memoryUsage > 85) {
      return { recommendedStrategy: 'lfu', efficiency: 92 };
    } else if (efficiency > 95) {
      return { recommendedStrategy: 'adaptive', efficiency: 97 };
    } else {
      return { recommendedStrategy: 'lru', efficiency: 89 };
    }
  }

  async exportConfiguration(): Promise<string> {
    const configs = await storage.getSystemConfigs();
    const exportData = {
      timestamp: new Date().toISOString(),
      version: '1.0',
      configurations: configs,
      systemContext: this.configContext,
    };

    return JSON.stringify(exportData, null, 2);
  }

  async importConfiguration(configData: string): Promise<void> {
    try {
      const data = JSON.parse(configData);

      if (!data.configurations || !Array.isArray(data.configurations)) {
        throw new Error('Invalid configuration data format');
      }

      const configsToApply: Record<string, any> = {};

      for (const config of data.configurations) {
        configsToApply[config.key] = config.value;
      }

      await this.applyBulkConfiguration(configsToApply);

      await alertService.createSystemAlert(
        'Configuration Imported',
        `Successfully imported ${data.configurations.length} configuration(s)`,
        'low'
      );

      logger.info('Configuration import completed successfully');
    } catch (error) {
      logger.error('Configuration import failed:', error);
      throw error;
    }
  }

  getStatus() {
    return {
      isInitialized: this.isInitialized,
      activeConfigs: this.activeConfigs.size,
      systemContext: this.configContext,
    };
  }
}

export const configService = new ConfigService();

server/services/gpuManager.ts.content:
import { storage } from "../storage";
import { alertService } from "./alertService";
import { logger } from "../utils/logger";
import type { GPU } from "@shared/schema";

interface GPUStatus {
  total: number;
  online: number;
  offline: number;
  warning: number;
  error: number;
}

class GPUManager {
  private isInitialized = false;
  private broadcast?: (event: string, data: any) => void;
  private monitoringInterval?: NodeJS.Timeout;
  private simulatedGPUs: Map<string, any> = new Map();

  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    logger.info('Initializing GPU Manager...');

    try {
      // Create initial GPU setup if none exist
      await this.setupInitialGPUs();

      // Start monitoring
      this.startMonitoring();

      this.isInitialized = true;
      logger.info('GPU Manager initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize GPU Manager:', error);
      throw error;
    }
  }

  setBroadcast(broadcastFn: (event: string, data: any) => void): void {
    this.broadcast = broadcastFn;
  }

  private async setupInitialGPUs(): Promise<void> {
    const existingGPUs = await storage.getGPUs();

    if (existingGPUs.length === 0) {
      logger.info('Setting up initial GPU configuration...');

      const initialGPUs = [
        { name: 'RTX 4090 #1', model: 'NVIDIA RTX 4090', hashRate: 14.2, temperature: 67, power: 320, memoryUsed: 18.5, memoryTotal: 24.0, utilizationRate: 89, status: 'online' },
        { name: 'RTX 4090 #2', model: 'NVIDIA RTX 4090', hashRate: 14.1, temperature: 71, power: 315, memoryUsed: 19.2, memoryTotal: 24.0, utilizationRate: 92, status: 'online' },
        { name: 'RTX 4090 #3', model: 'NVIDIA RTX 4090', hashRate: 12.8, temperature: 83, power: 340, memoryUsed: 20.1, memoryTotal: 24.0, utilizationRate: 78, status: 'warning' },
        { name: 'RTX 4080 #1', model: 'NVIDIA RTX 4080', hashRate: 11.5, temperature: 65, power: 280, memoryUsed: 14.2, memoryTotal: 16.0, utilizationRate: 85, status: 'online' },
        { name: 'RTX 4080 #2', model: 'NVIDIA RTX 4080', hashRate: 11.3, temperature: 69, power: 275, memoryUsed: 13.8, memoryTotal: 16.0, utilizationRate: 87, status: 'online' },
        { name: 'RTX 3090 #1', model: 'NVIDIA RTX 3090', hashRate: 10.2, temperature: 74, power: 350, memoryUsed: 22.1, memoryTotal: 24.0, utilizationRate: 82, status: 'online' },
      ];

      for (const gpuData of initialGPUs) {
        const gpu = await storage.createGPU(gpuData);
        this.simulatedGPUs.set(gpu.id, { ...gpuData, id: gpu.id });
      }
    } else {
      // Load existing GPUs into simulation
      for (const gpu of existingGPUs) {
        this.simulatedGPUs.set(gpu.id, gpu);
      }
    }
  }

  private startMonitoring(): void {
    this.monitoringInterval = setInterval(async () => {
      try {
        await this.updateGPUMetrics();
      } catch (error) {
        logger.error('Error updating GPU metrics:', error);
      }
    }, 5000); // Update every 5 seconds
  }

  private async updateGPUMetrics(): Promise<void> {
    const gpus = await storage.getGPUs();
    const updates: Promise<any>[] = [];

    for (const gpu of gpus) {
      const simGPU = this.simulatedGPUs.get(gpu.id);
      if (!simGPU) continue;

      // Simulate realistic GPU behavior
      const newMetrics = this.simulateGPUBehavior(simGPU);

      // Update database
      const updatePromise = storage.updateGPU(gpu.id, newMetrics);
      updates.push(updatePromise);

      // Update simulation state
      this.simulatedGPUs.set(gpu.id, { ...simGPU, ...newMetrics });
    }

    const updatedGPUs = await Promise.all(updates);

    // Broadcast updates
    if (this.broadcast) {
      this.broadcast('gpu_metrics_update', updatedGPUs.filter(Boolean));
    }

    // Check for alerts
    await this.checkGPUAlerts(updatedGPUs.filter(Boolean));
  }

  private simulateGPUBehavior(gpu: any): Partial<GPU> {
    const updates: Partial<GPU> = {};

    // Temperature simulation with realistic fluctuation
    const tempVariation = (Math.random() - 0.5) * 4; // Â±2Â°C variation
    let newTemp = gpu.temperature + tempVariation;

    // Temperature bounds based on load
    const minTemp = 45;
    const maxTemp = gpu.status === 'error' ? 95 : 90;
    newTemp = Math.max(minTemp, Math.min(maxTemp, newTemp));
    updates.temperature = Math.round(newTemp);

    // Hash rate based on temperature and status
    let hashRateMultiplier = 1.0;
    if (newTemp > 85) {
      hashRateMultiplier = 0.85; // Thermal throttling
    } else if (newTemp > 80) {
      hashRateMultiplier = 0.92;
    }

    const baseHashRate = gpu.model.includes('4090') ? 14.2 : gpu.model.includes('4080') ? 11.5 : 10.2;
    updates.hashRate = Math.round(baseHashRate * hashRateMultiplier * 10) / 10;

    // Power consumption based on load and temperature
    const basePower = gpu.model.includes('4090') ? 320 : gpu.model.includes('4080') ? 280 : 350;
    const powerVariation = (newTemp - 65) * 0.5 + (Math.random() - 0.5) * 20;
    updates.power = Math.max(200, Math.min(400, Math.round(basePower + powerVariation)));

    // Utilization rate
    const utilizationVariation = (Math.random() - 0.5) * 10;
    updates.utilizationRate = Math.max(60, Math.min(100, Math.round(gpu.utilizationRate + utilizationVariation)));

    // Memory usage simulation
    const memoryVariation = (Math.random() - 0.5) * 1.0;
    updates.memoryUsed = Math.max(8, Math.min(gpu.memoryTotal - 1, gpu.memoryUsed + memoryVariation));

    // Status determination
    if (newTemp > 90) {
      updates.status = 'error';
    } else if (newTemp > 80 || updates.utilizationRate < 70) {
      updates.status = 'warning';
    } else {
      updates.status = 'online';
    }

    return updates;
  }

  private async checkGPUAlerts(gpus: GPU[]): Promise<void> {
    for (const gpu of gpus) {
      // Temperature alerts
      if (gpu.temperature > 90) {
        await alertService.createAlert({
          type: 'error',
          title: 'Critical GPU Temperature',
          message: `${gpu.name} is critically overheating at ${gpu.temperature}Â°C`,
          source: 'gpu',
          sourceId: gpu.id,
          severity: 'critical',
        });
      } else if (gpu.temperature > 85) {
        await alertService.createAlert({
          type: 'warning',
          title: 'High GPU Temperature',
          message: `${gpu.name} temperature is ${gpu.temperature}Â°C`,
          source: 'gpu',
          sourceId: gpu.id,
          severity: 'high',
        });
      }

      // Performance alerts
      if (gpu.utilizationRate < 60) {
        await alertService.createAlert({
          type: 'warning',
          title: 'Low GPU Utilization',
          message: `${gpu.name} utilization is only ${gpu.utilizationRate}%`,
          source: 'gpu',
          sourceId: gpu.id,
          severity: 'medium',
        });
      }

      // Power alerts
      if (gpu.power > 380) {
        await alertService.createAlert({
          type: 'warning',
          title: 'High Power Consumption',
          message: `${gpu.name} consuming ${gpu.power}W`,
          source: 'gpu',
          sourceId: gpu.id,
          severity: 'medium',
        });
      }
    }
  }

  async optimizeAll(): Promise<void> {
    logger.info('Starting GPU optimization...');

    const gpus = await storage.getGPUs();
    const optimizationPromises = gpus.map(gpu => this.optimizeGPU(gpu.id));

    await Promise.all(optimizationPromises);

    await alertService.createAlert({
      type: 'success',
      title: 'GPU Optimization Complete',
      message: `Optimized ${gpus.length} GPUs for maximum performance`,
      source: 'system',
      severity: 'low',
    });

    logger.info('GPU optimization completed');
  }

  async optimizeGPU(gpuId: string): Promise<void> {
    const gpu = await storage.getGPU(gpuId);
    if (!gpu) return;

    // Simulate optimization by improving metrics
    const optimizedMetrics: Partial<GPU> = {
      temperature: Math.max(60, gpu.temperature - 5),
      utilizationRate: Math.min(95, gpu.utilizationRate + 10),
      status: gpu.temperature <= 85 ? 'online' : gpu.status,
    };

    // Recalculate hash rate based on optimized temperature
    const baseHashRate = gpu.model.includes('4090') ? 14.2 : gpu.model.includes('4080') ? 11.5 : 10.2;
    if (optimizedMetrics.temperature! <= 75) {
      optimizedMetrics.hashRate = baseHashRate;
    }

    await storage.updateGPU(gpuId, optimizedMetrics);

    // Update simulation state
    const simGPU = this.simulatedGPUs.get(gpuId);
    if (simGPU) {
      this.simulatedGPUs.set(gpuId, { ...simGPU, ...optimizedMetrics });
    }

    if (this.broadcast) {
      this.broadcast('gpu_optimized', { id: gpuId, ...optimizedMetrics });
    }
  }

  async getGPUStatus(): Promise<GPUStatus> {
    const gpus = await storage.getGPUs();

    return {
      total: gpus.length,
      online: gpus.filter(gpu => gpu.status === 'online').length,
      offline: gpus.filter(gpu => gpu.status === 'offline').length,
      warning: gpus.filter(gpu => gpu.status === 'warning').length,
      error: gpus.filter(gpu => gpu.status === 'error').length,
    };
  }

  getStatus() {
    return {
      isInitialized: this.isInitialized,
      monitoredGPUs: this.simulatedGPUs.size,
      isMonitoring: !!this.monitoringInterval,
    };
  }

  async shutdown(): Promise<void> {
    logger.info('Shutting down GPU Manager...');

    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
    }

    this.simulatedGPUs.clear();
    logger.info('GPU Manager shut down successfully');
  }
}

export const gpuManager = new GPUManager();

server/services/merkleTreeService.ts.content:
import { storage } from "../storage";
import { alertService } from "./alertService";
import { logger } from "../utils/logger";
import type { MerkleTreeConfig } from "@shared/schema";

interface MerkleTreePerformance {
  constructionSpeed: number;
  parallelEfficiency: number;
  cacheHitRate: number;
  memoryUtilization: number;
  averageDepth: number;
  optimizationLevel: number;
}

interface AlgorithmStatus {
  name: string;
  status: 'active' | 'optimizing' | 'inactive';
  performance: number;
  threadsUsed: number;
}

class MerkleTreeService {
  private isInitialized = false;
  private currentConfig?: MerkleTreeConfig;
  private performance: MerkleTreePerformance = {
    constructionSpeed: 8742,
    parallelEfficiency: 94.8,
    cacheHitRate: 96.2,
    memoryUtilization: 72.3,
    averageDepth: 16,
    optimizationLevel: 87,
  };
  private algorithms: Map<string, AlgorithmStatus> = new Map();

  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    logger.info('Initializing Merkle Tree Service...');

    try {
      // Load active configuration
      this.currentConfig = await storage.getActiveMerkleTreeConfig();

      if (!this.currentConfig) {
        // Create default configuration
        this.currentConfig = await this.createDefaultConfiguration();
      }

      // Initialize algorithms
      this.initializeAlgorithms();

      // Start performance monitoring
      this.startPerformanceMonitoring();

      this.isInitialized = true;
      logger.info('Merkle Tree Service initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize Merkle Tree Service:', error);
      throw error;
    }
  }

  private async createDefaultConfiguration(): Promise<MerkleTreeConfig> {
    const defaultConfig = {
      name: 'Adaptive Parallel Configuration',
      parallelThreads: 512,
      treeDepth: 'auto',
      cacheStrategy: 'adaptive',
      isActive: true,
      performance: this.performance,
    };

    return await storage.createMerkleTreeConfig(defaultConfig);
  }

  private initializeAlgorithms(): void {
    this.algorithms.set('PhaseNU', {
      name: 'PhaseNU Algorithm',
      status: 'active',
      performance: 94.2,
      threadsUsed: this.currentConfig?.parallelThreads || 512,
    });

    this.algorithms.set('AdaptiveRestructuring', {
      name: 'Adaptive Restructuring',
      status: 'optimizing',
      performance: 91.8,
      threadsUsed: Math.floor((this.currentConfig?.parallelThreads || 512) * 0.3),
    });

    this.algorithms.set('ParallelTraversal', {
      name: 'Parallel Merkle Tree Traversal',
      status: 'active',
      performance: 89.5,
      threadsUsed: Math.floor((this.currentConfig?.parallelThreads || 512) * 0.6),
    });
  }

  private startPerformanceMonitoring(): void {
    setInterval(() => {
      this.updatePerformanceMetrics();
    }, 10000); // Update every 10 seconds
  }

  private updatePerformanceMetrics(): void {
    // Simulate realistic performance fluctuations
    const baseSpeed = 8500;
    const speedVariation = (Math.random() - 0.5) * 500;
    this.performance.constructionSpeed = Math.round(baseSpeed + speedVariation);

    // Parallel efficiency based on thread utilization
    const threadUtilization = this.calculateThreadUtilization();
    this.performance.parallelEfficiency = Math.max(85, Math.min(98, 
      90 + threadUtilization * 0.08 + (Math.random() - 0.5) * 4
    ));

    // Cache hit rate simulation
    const cacheVariation = (Math.random() - 0.5) * 2;
    this.performance.cacheHitRate = Math.max(92, Math.min(99, 
      this.performance.cacheHitRate + cacheVariation
    ));

    // Memory utilization
    const memoryVariation = (Math.random() - 0.5) * 5;
    this.performance.memoryUtilization = Math.max(60, Math.min(85, 
      this.performance.memoryUtilization + memoryVariation
    ));

    // Update algorithm statuses
    this.updateAlgorithmStatuses();
  }

  private calculateThreadUtilization(): number {
    if (!this.currentConfig) return 0.8;

    const optimalThreads = 512;
    const currentThreads = this.currentConfig.parallelThreads;

    if (currentThreads === optimalThreads) return 1.0;
    if (currentThreads < optimalThreads) return currentThreads / optimalThreads;

    // Diminishing returns for too many threads
    const excess = currentThreads - optimalThreads;
    return 1.0 - (excess / (optimalThreads * 2)) * 0.3;
  }

  private updateAlgorithmStatuses(): void {
    const algorithms = Array.from(this.algorithms.keys());

    algorithms.forEach(key => {
      const algorithm = this.algorithms.get(key)!;

      // Simulate performance fluctuations
      const performanceVariation = (Math.random() - 0.5) * 3;
      algorithm.performance = Math.max(80, Math.min(98, 
        algorithm.performance + performanceVariation
      ));

      // Occasionally change status for realism
      if (Math.random() < 0.1) { // 10% chance every update
        const statuses: Array<'active' | 'optimizing' | 'inactive'> = ['active', 'optimizing'];
        algorithm.status = statuses[Math.floor(Math.random() * statuses.length)];
      }
    });
  }

  async applyConfiguration(configId: string): Promise<void> {
    logger.info(`Applying Merkle tree configuration: ${configId}`);

    try {
      const config = await storage.updateMerkleTreeConfig(configId, { isActive: true });
      if (!config) {
        throw new Error('Configuration not found');
      }

      this.currentConfig = config;

      // Restart algorithms with new configuration
      this.initializeAlgorithms();

      // Simulate configuration application time
      await new Promise(resolve => setTimeout(resolve, 2000));

      await alertService.createAlert({
        type: 'success',
        title: 'Merkle Tree Configuration Applied',
        message: `Configuration "${config.name}" is now active`,
        source: 'system',
        severity: 'low',
      });

      logger.info('Merkle tree configuration applied successfully');
    } catch (error) {
      logger.error('Failed to apply configuration:', error);
      throw error;
    }
  }

  async optimizeConfiguration(): Promise<void> {
    logger.info('Starting Merkle tree optimization...');

    try {
      if (!this.currentConfig) {
        throw new Error('No active configuration to optimize');
      }

      // Simulate optimization process
      const optimizationSteps = [
        'Analyzing current performance patterns',
        'Calculating optimal thread distribution',
        'Optimizing cache strategies',
        'Restructuring tree depth parameters',
        'Applying adaptive algorithms',
      ];

      for (const step of optimizationSteps) {
        logger.info(`Optimization: ${step}`);
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      // Calculate optimized parameters
      const optimizedThreads = this.calculateOptimalThreads();
      const optimizedCacheStrategy = this.selectOptimalCacheStrategy();

      const optimizedConfig = {
        parallelThreads: optimizedThreads,
        cacheStrategy: optimizedCacheStrategy,
        performance: {
          ...this.performance,
          parallelEfficiency: Math.min(98, this.performance.parallelEfficiency + 3),
          constructionSpeed: Math.round(this.performance.constructionSpeed * 1.1),
          cacheHitRate: Math.min(99, this.performance.cacheHitRate + 2),
        },
      };

      await storage.updateMerkleTreeConfig(this.currentConfig.id, optimizedConfig);
      this.currentConfig = { ...this.currentConfig, ...optimizedConfig };

      await alertService.createAlert({
        type: 'success',
        title: 'Merkle Tree Optimization Complete',
        message: 'Performance improvements have been applied successfully',
        source: 'system',
        severity: 'low',
      });

      logger.info('Merkle tree optimization completed successfully');
    } catch (error) {
      logger.error('Merkle tree optimization failed:', error);
      throw error;
    }
  }

  private calculateOptimalThreads(): number {
    // Simulate intelligent thread calculation based on GPU availability
    const baseThreads = 512;
    const performanceFactor = this.performance.parallelEfficiency / 100;
    const memoryUsageFactor = 1 - (this.performance.memoryUtilization / 100);

    const optimalThreads = Math.round(baseThreads * performanceFactor * (1 + memoryUsageFactor * 0.5));
    return Math.max(128, Math.min(1024, optimalThreads));
  }

  private selectOptimalCacheStrategy(): string {
    const hitRate = this.performance.cacheHitRate;
    const memoryUsage = this.performance.memoryUtilization;

    if (hitRate > 97 && memoryUsage < 70) {
      return 'adaptive';
    } else if (memoryUsage > 80) {
      return 'lfu'; // Least Frequently Used when memory is constrained
    } else {
      return 'lru'; // Least Recently Used as fallback
    }
  }

  getPerformanceMetrics(): MerkleTreePerformance {
    return { ...this.performance };
  }

  getAlgorithmStatuses(): AlgorithmStatus[] {
    return Array.from(this.algorithms.values());
  }

  getCurrentConfiguration(): MerkleTreeConfig | undefined {
    return this.currentConfig;
  }

  async createConfiguration(config: any): Promise<MerkleTreeConfig> {
    logger.info(`Creating new Merkle tree configuration: ${config.name}`);

    const newConfig = await storage.createMerkleTreeConfig({
      ...config,
      performance: this.performance,
    });

    await alertService.createAlert({
      type: 'info',
      title: 'New Configuration Created',
      message: `Merkle tree configuration "${config.name}" has been created`,
      source: 'system',
      severity: 'low',
    });

    return newConfig;
  }

  // Simulate advanced parallel processing algorithms
  async processTransactionBatch(transactions: any[], algorithm: string = 'PhaseNU'): Promise<{
    merkleRoot: string;
    processingTime: number;
    efficiency: number;
  }> {
    const startTime = Date.now();

    // Simulate parallel processing based on current configuration
    const batchSize = transactions.length;
    const threadsUsed = Math.min(this.currentConfig?.parallelThreads || 512, batchSize);

    // Calculate processing time based on parallel efficiency
    const baseProcessingTime = batchSize / 1000; // Base: 1000 tx/second
    const parallelSpeedup = threadsUsed * (this.performance.parallelEfficiency / 100);
    const processingTime = baseProcessingTime / parallelSpeedup;

    // Simulate actual processing delay
    await new Promise(resolve => setTimeout(resolve, Math.min(processingTime * 10, 100)));

    // Generate merkle root
    const merkleRoot = this.generateMerkleRoot(transactions);

    const actualProcessingTime = Date.now() - startTime;
    const efficiency = Math.min(100, (processingTime / actualProcessingTime) * 100);

    return {
      merkleRoot,
      processingTime: actualProcessingTime,
      efficiency,
    };
  }

  private generateMerkleRoot(transactions: any[]): string {
    // Simplified merkle root generation
    const crypto = require('crypto');
    const hash = crypto.createHash('sha256');

    // Combine all transaction hashes
    const combinedHash = transactions
      .map(tx => typeof tx === 'string' ? tx : JSON.stringify(tx))
      .join('');

    hash.update(combinedHash + Date.now());
    return hash.digest('hex');
  }

  getStatus() {
    return {
      isInitialized: this.isInitialized,
      activeConfig: this.currentConfig?.name || 'None',
      performance: this.performance,
      algorithms: this.getAlgorithmStatuses(),
    };
  }
}

export const merkleTreeService = new MerkleTreeService();

server/services/miningEngine.ts.content:
import { storage } from "../storage";
import { merkleTreeService } from "./merkleTreeService";
import { gpuManager } from "./gpuManager";
import { alertService } from "./alertService";
import { logger } from "../utils/logger";
import { MINING_CONSTANTS } from "../utils/constants";

interface MiningEngineStatus {
  isRunning: boolean;
  hashRate: number;
  efficiency: number;
  activeAlgorithm: string;
  parallelThreads: number;
  uptime: number;
}

class MiningEngine {
  private isInitialized = false;
  private isRunning = false;
  private startTime = Date.now();
  private currentHashRate = 0;
  private currentEfficiency = 0;
  private broadcast?: (event: string, data: any) => void;
  private metricsInterval?: NodeJS.Timeout;
  private processingInterval?: NodeJS.Timeout;

  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    logger.info('Initializing Mining Engine...');

    try {
      // Initialize merkle tree service
      await merkleTreeService.initialize();

      // Start metrics collection
      this.startMetricsCollection();

      // Start transaction processing simulation
      this.startTransactionProcessing();

      this.isRunning = true;
      this.isInitialized = true;

      logger.info('Mining Engine initialized successfully');

      await alertService.createAlert({
        type: 'info',
        title: 'Mining Engine Started',
        message: 'Adaptive parallel Merkle tree mining engine is now operational',
        source: 'system',
        severity: 'low',
      });
    } catch (error) {
      logger.error('Failed to initialize Mining Engine:', error);
      throw error;
    }
  }

  setBroadcast(broadcastFn: (event: string, data: any) => void): void {
    this.broadcast = broadcastFn;
  }

  private startMetricsCollection(): void {
    this.metricsInterval = setInterval(async () => {
      try {
        await this.collectAndStoreMetrics();
      } catch (error) {
        logger.error('Error collecting metrics:', error);
      }
    }, MINING_CONSTANTS.METRICS_INTERVAL);
  }

  private startTransactionProcessing(): void {
    this.processingInterval = setInterval(async () => {
      try {
        await this.processTransactionBatch();
      } catch (error) {
        logger.error('Error processing transaction batch:', error);
      }
    }, MINING_CONSTANTS.PROCESSING_INTERVAL);
  }

  private async collectAndStoreMetrics(): Promise<void> {
    const gpus = await storage.getGPUs();
    const activeGPUs = gpus.filter(gpu => gpu.status === 'online');

    // Calculate aggregated metrics
    this.currentHashRate = activeGPUs.reduce((sum, gpu) => sum + gpu.hashRate, 0);

    // Simulate efficiency based on various factors
    const tempEfficiency = this.calculateTemperatureEfficiency(activeGPUs);
    const loadEfficiency = this.calculateLoadEfficiency();
    const configEfficiency = await this.calculateConfigurationEfficiency();

    this.currentEfficiency = (tempEfficiency + loadEfficiency + configEfficiency) / 3;

    // Generate revenue calculation (simplified)
    const dailyRevenue = this.currentHashRate * MINING_CONSTANTS.REVENUE_PER_TH * 24;

    // System resource usage simulation
    const cpuUsage = Math.random() * 30 + 20; // 20-50%
    const memoryUsage = Math.random() * 20 + 60; // 60-80%
    const storageUsage = Math.random() * 10 + 40; // 40-50%
    const networkLoad = Math.random() * 30 + 60; // 60-90%

    // Transaction processing metrics
    const baseTransactions = MINING_CONSTANTS.BASE_TRANSACTIONS_PER_SECOND;
    const efficiencyMultiplier = this.currentEfficiency / 100;
    const transactionsProcessed = Math.floor(baseTransactions * efficiencyMultiplier * activeGPUs.length);
    const transactionsPending = Math.floor(Math.random() * 5000 + 2000);

    const metrics = {
      totalHashRate: this.currentHashRate,
      treeEfficiency: this.currentEfficiency,
      activeGPUs: activeGPUs.length,
      dailyRevenue,
      cpuUsage,
      memoryUsage,
      storageUsage,
      networkLoad,
      transactionsProcessed,
      transactionsPending,
    };

    await storage.createSystemMetric(metrics);

    // Broadcast real-time updates
    if (this.broadcast) {
      this.broadcast('metrics_update', metrics);
    }

    // Check for performance alerts
    await this.checkPerformanceAlerts(metrics);
  }

  private calculateTemperatureEfficiency(gpus: any[]): number {
    if (gpus.length === 0) return 0;

    const avgTemp = gpus.reduce((sum, gpu) => sum + gpu.temperature, 0) / gpus.length;
    const optimalTemp = 65;
    const maxTemp = 85;

    if (avgTemp <= optimalTemp) return 100;
    if (avgTemp >= maxTemp) return 60;

    return 100 - ((avgTemp - optimalTemp) / (maxTemp - optimalTemp)) * 40;
  }

  private calculateLoadEfficiency(): number {
    // Simulate load-based efficiency
    const currentLoad = Math.random() * 100;
    const optimalLoad = 85;

    if (currentLoad <= optimalLoad) {
      return 95 + (currentLoad / optimalLoad) * 5;
    } else {
      return 100 - ((currentLoad - optimalLoad) / (100 - optimalLoad)) * 15;
    }
  }

  private async calculateConfigurationEfficiency(): Promise<number> {
    const config = await storage.getActiveMerkleTreeConfig();
    if (!config) return 85;

    // Simulate configuration-based efficiency improvements
    let efficiency = 85;

    // Parallel threads optimization
    const optimalThreads = 512;
    if (config.parallelThreads === optimalThreads) {
      efficiency += 5;
    } else if (Math.abs(config.parallelThreads - optimalThreads) <= 128) {
      efficiency += 3;
    }

    // Cache strategy bonus
    if (config.cacheStrategy === 'adaptive') {
      efficiency += 7;
    } else if (config.cacheStrategy === 'lru') {
      efficiency += 3;
    }

    // Tree depth optimization
    if (config.treeDepth === 'auto') {
      efficiency += 3;
    }

    return Math.min(efficiency, 100);
  }

  private async processTransactionBatch(): Promise<void> {
    const config = await storage.getActiveMerkleTreeConfig();
    const gpus = await storage.getGPUs();
    const activeGPUs = gpus.filter(gpu => gpu.status === 'online');

    if (activeGPUs.length === 0) return;

    // Simulate adaptive parallel merkle tree construction
    const batchSize = Math.floor(Math.random() * 10000) + 5000; // 5k-15k transactions
    const algorithm = config?.cacheStrategy === 'adaptive' ? 'PhaseNU-Adaptive' : 'PhaseNU-Standard';

    // Simulate processing time based on batch size and efficiency
    const baseProcessingTime = batchSize / MINING_CONSTANTS.BASE_TRANSACTIONS_PER_SECOND;
    const efficiencyMultiplier = this.currentEfficiency / 100;
    const processingTime = baseProcessingTime / efficiencyMultiplier;

    // Generate merkle root (simulated)
    const merkleRoot = this.generateMerkleRoot(batchSize);

    // Determine network type based on batch characteristics
    const networkType = batchSize > 8000 ? 'bitcoin' : 'ethereum';

    const batch = {
      batchSize,
      processingTime,
      merkleRoot,
      algorithm,
      networkType,
      efficiency: this.currentEfficiency,
      gpuIds: activeGPUs.slice(0, Math.ceil(activeGPUs.length * 0.7)).map(gpu => gpu.id), // Use 70% of GPUs
    };

    try {
      await storage.createTransactionBatch(batch);
    } catch (error) {
      // Transaction batch creation failed - log for debugging but don't crash
      logger.error('Failed to store transaction batch:', error);
      return;
    }

    // Broadcast processing update
    if (this.broadcast) {
      this.broadcast('batch_processed', batch);
    }
  }

  private generateMerkleRoot(batchSize: number): string {
    // Simulate merkle root generation
    const hash = require('crypto').createHash('sha256');
    hash.update(`batch-${batchSize}-${Date.now()}`);
    return hash.digest('hex');
  }

  private async checkPerformanceAlerts(metrics: any): Promise<void> {
    // High temperature alert
    const gpus = await storage.getGPUs();
    const hotGPUs = gpus.filter(gpu => gpu.temperature > 80);

    for (const gpu of hotGPUs) {
      if (gpu.temperature > 85) {
        await alertService.createAlert({
          type: 'error',
          title: 'Critical GPU Temperature',
          message: `${gpu.name} is running at ${gpu.temperature}Â°C - immediate attention required`,
          source: 'gpu',
          sourceId: gpu.id,
          severity: 'critical',
        });
      } else {
        await alertService.createAlert({
          type: 'warning',
          title: 'GPU Temperature Warning',
          message: `${gpu.name} is running at ${gpu.temperature}Â°C`,
          source: 'gpu',
          sourceId: gpu.id,
          severity: 'medium',
        });
      }
    }

    // Low efficiency alert
    if (metrics.treeEfficiency < 85) {
      await alertService.createAlert({
        type: 'warning',
        title: 'Low Mining Efficiency',
        message: `Current efficiency is ${metrics.treeEfficiency.toFixed(1)}% - consider optimization`,
        source: 'system',
        severity: 'medium',
      });
    }

    // High system load alert
    if (metrics.cpuUsage > 90 || metrics.memoryUsage > 95) {
      await alertService.createAlert({
        type: 'warning',
        title: 'High System Load',
        message: `CPU: ${metrics.cpuUsage.toFixed(1)}%, Memory: ${metrics.memoryUsage.toFixed(1)}%`,
        source: 'system',
        severity: 'high',
      });
    }
  }

  async optimizePerformance(): Promise<void> {
    logger.info('Starting performance optimization...');

    try {
      // Optimize merkle tree configuration
      await merkleTreeService.optimizeConfiguration();

      // Optimize GPU settings
      await gpuManager.optimizeAll();

      // Update metrics
      await this.collectAndStoreMetrics();

      await alertService.createAlert({
        type: 'success',
        title: 'Performance Optimization Complete',
        message: 'System has been optimized for maximum efficiency',
        source: 'system',
        severity: 'low',
      });

      logger.info('Performance optimization completed successfully');
    } catch (error) {
      logger.error('Performance optimization failed:', error);
      throw error;
    }
  }

  getStatus(): MiningEngineStatus {
    return {
      isRunning: this.isRunning,
      hashRate: this.currentHashRate,
      efficiency: this.currentEfficiency,
      activeAlgorithm: 'PhaseNU-Adaptive',
      parallelThreads: 512,
      uptime: Date.now() - this.startTime,
    };
  }

  async shutdown(): Promise<void> {
    logger.info('Shutting down Mining Engine...');

    this.isRunning = false;

    if (this.metricsInterval) {
      clearInterval(this.metricsInterval);
    }

    if (this.processingInterval) {
      clearInterval(this.processingInterval);
    }

    logger.info('Mining Engine shut down successfully');
  }
}

export const miningEngine = new MiningEngine();

