import { Card, CardContent } from "@/components/ui/card";
import { LucideIcon } from "lucide-react";
import { TrendingUp, TrendingDown } from "lucide-react";

interface MetricsCardProps {
  title: string;
  value: string;
  change?: number;
  status?: string;
  icon: LucideIcon;
  color: 'emerald' | 'blue' | 'purple' | 'amber' | 'red';
  'data-testid'?: string;
}

const colorClasses = {
  emerald: 'bg-emerald-500/10 text-emerald-400',
  blue: 'bg-blue-500/10 text-blue-400',
  purple: 'bg-purple-500/10 text-purple-400',
  amber: 'bg-amber-500/10 text-amber-400',
  red: 'bg-red-500/10 text-red-400',
};

const iconColorClasses = {
  emerald: 'text-emerald-400',
  blue: 'text-blue-400',
  purple: 'text-purple-400',
  amber: 'text-amber-400',
  red: 'text-red-400',
};

export default function MetricsCard({ 
  title, 
  value, 
  change, 
  status, 
  icon: Icon, 
  color,
  'data-testid': testId 
}: MetricsCardProps) {
  const isPositiveChange = change !== undefined && change > 0;
  const isNegativeChange = change !== undefined && change < 0;

  return (
    <Card className="bg-slate-800 border-slate-700 metric-card" data-testid={testId}>
      <CardContent className="p-6">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-slate-400 text-sm">{title}</p>
            <p className="text-2xl font-bold text-slate-50" data-testid={`${testId}-value`}>
              {value}
            </p>
            {change !== undefined ? (
              <div className="flex items-center space-x-1 mt-1">
                {isPositiveChange ? (
                  <TrendingUp className="h-3 w-3 text-emerald-400" />
                ) : isNegativeChange ? (
                  <TrendingDown className="h-3 w-3 text-red-400" />
                ) : null}
                <span 
                  className={`text-sm ${
                    isPositiveChange ? 'text-emerald-400' : 
                    isNegativeChange ? 'text-red-400' : 'text-slate-400'
                  }`}
                  data-testid={`${testId}-change`}
                >
                  {change > 0 ? '+' : ''}{change.toFixed(1)}%
                </span>
              </div>
            ) : status ? (
              <div className="flex items-center space-x-1 mt-1">
                <div className="w-2 h-2 bg-emerald-500 rounded-full" />
                <span className="text-emerald-400 text-sm" data-testid={`${testId}-status`}>
                  {status}
                </span>
              </div>
            ) : null}
          </div>
          <div className={`w-12 h-12 rounded-lg flex items-center justify-center ${colorClasses[color]}`}>
            <Icon className={`h-6 w-6 ${iconColorClasses[color]}`} />
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/MiningPo.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import StatusIndicator from "@/components/ui/status-indicator";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Plus, Activity, Signal, Users } from "lucide-react";

interface MiningPool {
  id: string;
  name: string;
  url: string;
  port: number;
  username: string;
  difficulty?: number;
  latency: number;
  status: 'connected' | 'disconnected' | 'standby';
  isActive: boolean;
  priority: number;
  workers: number;
}

export default function MiningPoolStatus() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: pools, isLoading } = useQuery({
    queryKey: ['/api/mining-pools'],
  });

  const activatePoolMutation = useMutation({
    mutationFn: (poolId: string) => 
      apiRequest('PUT', `/api/mining-pools/${poolId}/activate`, {}),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/mining-pools'] });
      toast({
        title: "Pool Activated",
        description: "Mining pool has been switched successfully.",
      });
    },
  });

  const activePool = pools?.find((pool: MiningPool) => pool.isActive);
  const standbyPools = pools?.filter((pool: MiningPool) => !pool.isActive) || [];

  const getLatencyColor = (latency: number) => {
    if (latency < 20) return 'text-emerald-400';
    if (latency < 50) return 'text-amber-400';
    return 'text-red-400';
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="h-24 bg-slate-700 rounded-lg animate-pulse" />
        <div className="space-y-2">
          {[...Array(2)].map((_, i) => (
            <div key={i} className="h-16 bg-slate-700 rounded-lg animate-pulse" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4" data-testid="mining-pool-status">
      {/* Active Pool */}
      {activePool ? (
        <div className="pool-active rounded-lg p-4 border">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-2">
              <StatusIndicator status="online" />
              <span className="text-emerald-400 text-sm font-medium" data-testid="active-pool-name">
                {activePool.name}
              </span>
            </div>
            <Badge className="bg-emerald-500/10 text-emerald-400 border-emerald-500/20">
              Connected
            </Badge>
          </div>
          <div className="space-y-2 text-xs">
            <div className="flex justify-between">
              <span className="text-slate-300">Workers</span>
              <span className="text-white" data-testid="active-pool-workers">
                {activePool.workers}/24
              </span>
            </div>
            <div className="flex justify-between">
              <span className="text-slate-300">Difficulty</span>
              <span className="text-white" data-testid="active-pool-difficulty">
                {activePool.difficulty ? `${(activePool.difficulty / 1e12).toFixed(1)}T` : 'N/A'}
              </span>
            </div>
            <div className="flex justify-between">
              <span className="text-slate-300">Latency</span>
              <span className={getLatencyColor(activePool.latency)} data-testid="active-pool-latency">
                {activePool.latency}ms
              </span>
            </div>
          </div>
        </div>
      ) : (
        <div className="pool-disconnected rounded-lg p-4 border text-center">
          <div className="text-red-400 text-sm">No Active Pool</div>
          <div className="text-slate-500 text-xs mt-1">Please activate a mining pool</div>
        </div>
      )}

      {/* Backup Pools */}
      <div className="space-y-2">
        <h4 className="text-slate-300 text-sm font-medium">Backup Pools</h4>
        {standbyPools.slice(0, 2).map((pool: MiningPool) => (
          <div
            key={pool.id}
            className="pool-standby rounded-lg p-3 border flex items-center justify-between"
            data-testid={`backup-pool-${pool.id}`}
          >
            <div>
              <div className="text-slate-200 text-sm" data-testid={`backup-pool-name-${pool.id}`}>
                {pool.name}
              </div>
              <div className="text-xs text-slate-400">
                {pool.status} â€¢ Priority #{pool.priority}
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <div className={`text-xs ${getLatencyColor(pool.latency)}`}>
                {pool.latency}ms
              </div>
              <Button
                size="sm"
                variant="outline"
                onClick={() => activatePoolMutation.mutate(pool.id)}
                disabled={activatePoolMutation.isPending}
                className="text-xs h-6 px-2"
                data-testid={`activate-pool-${pool.id}`}
              >
                <Activity className="h-3 w-3 mr-1" />
                Switch
              </Button>
            </div>
          </div>
        ))}
      </div>

      <Button 
        variant="outline" 
        size="sm" 
        className="w-full text-slate-200"
        data-testid="button-add-pool"
      >
        <Plus className="h-4 w-4 mr-2" />
        Add Pool
      </Button>
    </div>
  );
}

client/src/components/dashboard/SystemAl.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useWebSocket } from "@/hooks/useWebSocket";
import { 
  AlertTriangle, 
  Info, 
  CheckCircle, 
  XCircle, 
  X,
  Trash2
} from "lucide-react";
import { useEffect, useState } from "react";

interface Alert {
  id: string;
  type: 'info' | 'warning' | 'error' | 'success';
  title: string;
  message: string;
  source?: string;
  sourceId?: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  isRead: boolean;
  isResolved: boolean;
  createdAt: string;
}

export default function SystemAlerts() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { lastMessage } = useWebSocket();
  const [alerts, setAlerts] = useState<Alert[]>([]);

  const { data: alertsData, isLoading } = useQuery({
    queryKey: ['/api/alerts'],
    queryParams: { limit: 10 },
  });

  const clearAllMutation = useMutation({
    mutationFn: () => apiRequest('DELETE', '/api/alerts', {}),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/alerts'] });
      setAlerts([]);
      toast({
        title: "Alerts Cleared",
        description: "All alerts have been cleared successfully.",
      });
    },
  });

  const markAsReadMutation = useMutation({
    mutationFn: (alertId: string) => 
      apiRequest('PUT', `/api/alerts/${alertId}/read`, {}),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/alerts'] });
    },
  });

  useEffect(() => {
    if (alertsData) {
      setAlerts(alertsData);
    }
  }, [alertsData]);

  useEffect(() => {
    if (lastMessage?.event === 'new_alert') {
      const newAlert = lastMessage.data;
      setAlerts(prev => [newAlert, ...prev.slice(0, 9)]);
    }

    if (lastMessage?.event === 'alerts_cleared') {
      setAlerts([]);
    }
  }, [lastMessage]);

  const getAlertIcon = (type: string, severity: string) => {
    switch (type) {
      case 'error':
        return <XCircle className="h-4 w-4 text-red-400" />;
      case 'warning':
        return <AlertTriangle className="h-4 w-4 text-amber-400" />;
      case 'success':
        return <CheckCircle className="h-4 w-4 text-emerald-400" />;
      default:
        return <Info className="h-4 w-4 text-blue-400" />;
    }
  };

  const getAlertBorderColor = (type: string, severity: string) => {
    if (severity === 'critical') return 'border-red-500/20 bg-red-500/10';
    switch (type) {
      case 'error':
        return 'border-red-500/20 bg-red-500/10';
      case 'warning':
        return 'border-amber-500/20 bg-amber-500/10';
      case 'success':
        return 'border-emerald-500/20 bg-emerald-500/10';
      default:
        return 'border-blue-500/20 bg-blue-500/10';
    }
  };

  const getSeverityBadgeColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'bg-red-500/10 text-red-400 border-red-500/20';
      case 'high':
        return 'bg-amber-500/10 text-amber-400 border-amber-500/20';
      case 'medium':
        return 'bg-blue-500/10 text-blue-400 border-blue-500/20';
      default:
        return 'bg-slate-500/10 text-slate-400 border-slate-500/20';
    }
  };

  const handleMarkAsRead = (alertId: string) => {
    markAsReadMutation.mutate(alertId);
    setAlerts(prev => 
      prev.map(alert => 
        alert.id === alertId ? { ...alert, isRead: true } : alert
      )
    );
  };

  const formatTimeAgo = (dateString: string) => {
    const now = new Date();
    const alertTime = new Date(dateString);
    const diffInMinutes = Math.floor((now.getTime() - alertTime.getTime()) / (1000 * 60));

    if (diffInMinutes < 1) return 'Just now';
    if (diffInMinutes < 60) return `${diffInMinutes} minutes ago`;
    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours} hours ago`;
    const diffInDays = Math.floor(diffInHours / 24);
    return `${diffInDays} days ago`;
  };

  if (isLoading) {
    return (
      <div className="space-y-3">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="h-16 bg-slate-700 rounded-lg animate-pulse" />
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-4" data-testid="system-alerts">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold text-slate-50">Recent Alerts</h3>
        {alerts.length > 0 && (
          <Button
            variant="outline"
            size="sm"
            onClick={() => clearAllMutation.mutate()}
            disabled={clearAllMutation.isPending}
            className="text-slate-400 hover:text-white text-xs"
            data-testid="button-clear-alerts"
          >
            <Trash2 className="h-3 w-3 mr-1" />
            Clear All
          </Button>
        )}
      </div>

      <div className="space-y-3">
        {alerts.length === 0 ? (
          <div className="text-center py-8">
            <CheckCircle className="h-12 w-12 text-emerald-400 mx-auto mb-2" />
            <p className="text-slate-400">No active alerts</p>
            <p className="text-slate-500 text-sm">System is running smoothly</p>
          </div>
        ) : (
          alerts.map((alert) => (
            <div
              key={alert.id}
              className={`flex items-start space-x-3 p-3 rounded-lg border ${getAlertBorderColor(alert.type, alert.severity)} ${
                !alert.isRead ? 'ring-1 ring-blue-500/20' : ''
              }`}
              data-testid={`alert-${alert.id}`}
            >
              <div className="flex-shrink-0 mt-0.5">
                {getAlertIcon(alert.type, alert.severity)}
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-center justify-between mb-1">
                  <div className="flex items-center space-x-2">
                    <h4 className="text-sm font-medium text-slate-50" data-testid={`alert-title-${alert.id}`}>
                      {alert.title}
                    </h4>
                    <Badge className={getSeverityBadgeColor(alert.severity)}>
                      {alert.severity}
                    </Badge>
                  </div>

                  <div className="flex items-center space-x-1">
                    {!alert.isRead && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleMarkAsRead(alert.id)}
                        className="h-6 w-6 p-0 text-slate-400 hover:text-white"
                        data-testid={`mark-read-${alert.id}`}
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    )}
                  </div>
                </div>

                <p className="text-xs text-slate-300 mb-2" data-testid={`alert-message-${alert.id}`}>
                  {alert.message}
                </p>

                <div className="flex items-center justify-between">
                  <span className="text-xs text-slate-400" data-testid={`alert-time-${alert.id}`}>
                    {formatTimeAgo(alert.createdAt)}
                  </span>

                  {alert.source && (
                    <span className="text-xs text-slate-500 capitalize">
                      {alert.source}
                      {alert.sourceId && ` â€¢ ${alert.sourceId.slice(0, 8)}`}
                    </span>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* System Health Summary */}
      <div className="mt-6 p-4 bg-slate-700 rounded-lg">
        <div className="flex items-center justify-between mb-2">
          <span className="text-slate-200 text-sm font-medium">System Health</span>
          <span className="text-emerald-400 text-sm" data-testid="system-health">
            Excellent
          </span>
        </div>
        <div className="grid grid-cols-3 gap-2 text-xs">
          <div className="text-center">
            <div className="text-slate-400">CPU</div>
            <div className="text-white" data-testid="system-cpu">34%</div>
          </div>
          <div className="text-center">
            <div className="text-slate-400">Memory</div>
            <div className="text-white" data-testid="system-memory">67%</div>
          </div>
          <div className="text-center">
            <div className="text-slate-400">Storage</div>
            <div className="text-white" data-testid="system-storage">42%</div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/dashboard/Transact.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Bitcoin, Zap } from "lucide-react";
import { useWebSocket } from "@/hooks/useWebSocket";
import { useEffect, useState } from "react";

interface TransactionStats {
  processed: number;
  pending: number;
  bitcoinTxPerSec: number;
  ethereumTxPerSec: number;
  bitcoinEfficiency: number;
  ethereumEfficiency: number;
  networkLoad: number;
}

export default function TransactionProcessor() {
  const [stats, setStats] = useState<TransactionStats>({
    processed: 12847,
    pending: 3924,
    bitcoinTxPerSec: 8432,
    ethereumTxPerSec: 4415,
    bitcoinEfficiency: 94.2,
    ethereumEfficiency: 91.8,
    networkLoad: 78,
  });

  const { data: transactionData } = useQuery({
    queryKey: ['/api/analytics/transactions', 1],
  });

  const { lastMessage } = useWebSocket();

  useEffect(() => {
    if (lastMessage?.event === 'batch_processed') {
      const batch = lastMessage.data;
      setStats(prev => ({
        ...prev,
        processed: prev.processed + batch.batchSize,
        pending: Math.max(0, prev.pending - Math.floor(batch.batchSize * 0.1)),
      }));
    }

    if (lastMessage?.event === 'metrics_update') {
      const metrics = lastMessage.data;
      setStats(prev => ({
        ...prev,
        processed: metrics.transactionsProcessed || prev.processed,
        pending: metrics.transactionsPending || prev.pending,
        networkLoad: metrics.networkLoad || prev.networkLoad,
      }));
    }
  }, [lastMessage]);

  return (
    <div className="space-y-6" data-testid="transaction-processor">
      {/* Transaction Counters */}
      <div className="grid grid-cols-2 gap-4">
        <Card className="bg-slate-700 border-slate-600">
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-emerald-400" data-testid="transactions-processed">
              {stats.processed.toLocaleString()}
            </div>
            <div className="text-sm text-slate-400">Transactions/sec</div>
          </CardContent>
        </Card>

        <Card className="bg-slate-700 border-slate-600">
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-blue-400" data-testid="transactions-pending">
              {stats.pending.toLocaleString()}
            </div>
            <div className="text-sm text-slate-400">Pending</div>
          </CardContent>
        </Card>
      </div>

      {/* Network Processing */}
      <div className="space-y-3">
        <div className="flex items-center justify-between p-3 bg-slate-700 rounded-lg">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-blue-500/10 rounded-lg flex items-center justify-center">
              <Bitcoin className="text-blue-400 h-4 w-4" />
            </div>
            <div>
              <div className="text-slate-200 text-sm font-medium">Bitcoin Network</div>
              <div className="text-xs text-slate-400" data-testid="bitcoin-processing">
                Processing: {stats.bitcoinTxPerSec.toLocaleString()} tx/s
              </div>
            </div>
          </div>
          <div className="text-emerald-400 text-sm" data-testid="bitcoin-efficiency">
            {stats.bitcoinEfficiency}%
          </div>
        </div>

        <div className="flex items-center justify-between p-3 bg-slate-700 rounded-lg">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-purple-500/10 rounded-lg flex items-center justify-center">
              <Zap className="text-purple-400 h-4 w-4" />
            </div>
            <div>
